import React, { useState } from "react";

const Chunk = ({ start, end, duration, progressTime, color, topic, onProgressChange }) => {
  const [isHovered, setIsHovered] = useState(false);

  const chunkWidth = `${((end - start) / duration) * 100}%`;
  const chunkLeft = `${(start / duration) * 100}%`;

  const chunkStyle = {
    width: chunkWidth,
    left: chunkLeft,
    backgroundColor: color,
    position: "absolute"
  };

  const progressBarStyle = {
    width: "100%",
    height: "10px",
    backgroundColor: "#ccc",
    position: "relative",
    cursor: "pointer"
  };

  const pointerStyle = {
    position: "absolute",
    top: "-5px",
    left: `${((progressTime - start) / duration) * 100}%`,
    width: "10px",
    height: "20px",
    borderRadius: "5px",
    backgroundColor: "#000"
  };

  const handlePointerDrag = e => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const progress = x / rect.width;
    const progressTime = start + progress * duration;
    onProgressChange(progressTime);
  };

  return (
    <div className="chunk-container" style={{ position: "relative" }}>
      <div
        className="chunk"
        style={chunkStyle}
        onMouseOver={() => setIsHovered(true)}
        onMouseOut={() => setIsHovered(false)}
      >
        {isHovered && <p id="topic">{topic}</p>}
      </div>
      <div className="progress-bar" style={progressBarStyle} onClick={handlePointerDrag}>
        <div className="pointer" style={pointerStyle} draggable="true" onDrag={handlePointerDrag}></div>
      </div>
    </div>
  );
};

export default Chunk;
