import React, { useState } from "react";
import ReactPlayer from "react-player";
import { useLocation } from "react-router-dom";

function VideoDetails() {
  const [instance, setInstance] = useState(null);

  const location = useLocation();

  let video_url = location.state;
  video_url = video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)

  const handleStart = (startTime) => {
    if (instance) {
      if (isNaN(startTime)) {
        startTime = "0.0";
      }
      instance.seekTo(startTime);
    }
  };

  return (
    <div className="video-player">
      <ReactPlayer
        ref={setInstance}
        url={video_url}
        playing
        controls
        onStart={handleStart}
      />

      <button onClick={() => handleStart(12.7)}>Seek to 12.7</button>
      <button onClick={() => handleStart(42.65)}>Seek to 42.65</button>
    </div>
  );
}

export default VideoDetails;
