import React, { useRef, useState, useEffect } from "react";
import Chunk from "./Chunk";

const ProgressBar = ({ duration, onTimeUpdate, chunks }) => {
  const [currentTime, setCurrentTime] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const progressBarRef = useRef(null);

  const handleProgressClick = (event) => {
    const progressBar = progressBarRef.current;
    const progressWidth = progressBar.offsetWidth;
    const progressClicked = event.clientX - progressBar.getBoundingClientRect().left;
    const newTime = (progressClicked / progressWidth) * duration;
    onTimeUpdate(newTime);
  };

  const handleDragStart = () => {
    setIsDragging(true);
  };

  const handleDragEnd = () => {
    setIsDragging(false);
  };

  const handleDragMove = (event) => {
    if (isDragging) {
      const progressBar = progressBarRef.current;
      const progressWidth = progressBar.offsetWidth;
      const progressClicked =
        event.clientX - progressBar.getBoundingClientRect().left;
      const newTime = (progressClicked / progressWidth) * duration;
      onTimeUpdate(newTime);
    }
  };

  const handleFullScreenChange = () => {
    if (document.fullscreenElement === null) {
      // Exit full-screen mode if the progress bar is being interacted with
      setIsDragging(false);
    }
  };

  const progress = (currentTime / duration) * 100;
  let chunksToShow = [];

  chunks.forEach((chunk) => {
    const chunkClass =
      progress >= chunk.start && progress <= chunk.end
        ? "chunk active"
        : "chunk";

    if (progress >= chunk.end) {
      chunksToShow.push(
        <div className={chunkClass} key={`${chunk.start}-${chunk.end}`} />
      );
    }
  });

  useEffect(() => {
    const progressBar = progressBarRef.current;

    const handleTimeUpdate = () => {
      setCurrentTime(progressBar.currentTime);
    };

    const handleProgressBarClick = (event) => {
      event.preventDefault();
      const clickedTime = (event.offsetX / progressBar.offsetWidth) * progressBar.duration;
      progressBar.currentTime = clickedTime;
    };
  
    progressBar.addEventListener("timeupdate", handleTimeUpdate);
    progressBar.addEventListener("click", handleProgressBarClick);
    document.addEventListener("fullscreenchange", handleFullScreenChange);
  
    return () => {
      progressBar.removeEventListener("timeupdate", handleTimeUpdate);
      progressBar.removeEventListener("click", handleProgressBarClick);
      document.removeEventListener("fullscreenchange", handleFullScreenChange);
    };
  }, []);

  const renderChunks = () => {
    return chunks.map((chunk, index) => (
      <Chunk key={index} {...chunk} duration={duration} />
    ));
  };

  return (
    <div
      className="progress-bar"
      ref={progressBarRef}
      onClick={handleProgressClick}
      onMouseDown={handleDragStart}
      onMouseUp={handleDragEnd}
      onMouseMove={handleDragMove}
    >
      {renderChunks()}
    </div>
  );
};

export default ProgressBar;
