import React, { useState } from "react"

const SearchBar = (props) => {
  const [searchData, setSearchData] = useState("");

  const handleChange = (e) => {
    window.scrollTo(0, 0);
    setSearchData(e.target.value);
  };

  const handleSubmit = (e) => {
    window.scrollTo(0, 0);
    e.preventDefault();

    if(searchData) {
      document.getElementById('searchData').style.width = "13.5rem";
    }
    
    props.onSubmit(searchData);
  };

  return (
    <div className="search-input">
      <form onSubmit={handleSubmit}>
        <input type="text" id="searchData" placeholder="Search" onChange={handleChange} autocomplete="off"/>
        <button type="submit" id="search-btn" class="fa fa-search"></button>
      </form>
    </div>
  )
}

export default SearchBar
