import { useState } from 'react'
import Video from './SubComponents/Video';
import VideoContent from './SubComponents/VideoContent';
import SearchBar from './SubComponents/SearchBar';
import Faculty from './SubComponents/Faculty';
import Domain from './SubComponents/Faculty';

function Content() {
  //Search bar
  const getSearchData = (data) => {
    console.log("Searched data is: ",data)
  }


  //Faculty
  const getFacultyChecked = (data) => {
    console.log("Selected faculty is: ",data)
  }
  


  //Domain
  const [domainchecked, setDomainChecked] = useState([]);
  const domainList = ["Social", "History", "Physics", "Chemistry", "Mathematics", "Finance",]

  const handledomainCheck = (event) => {
    var updatedfacultyList = [...domainchecked];

    if(event.target.checked) {
      updatedfacultyList = [...domainchecked, event.target.value];
    } 
    else {
      updatedfacultyList.splice(domainchecked.indexOf(event.target.value), 1);
    }
    setDomainChecked(updatedfacultyList);
  };

  const domaincheckedItems = domainchecked.length 
    ? domainchecked.reduce((total, item) => {
        return total + ", " + item;
      })
    : "";

  const isdomainChecked = (item) =>
    domainchecked.includes(item) ? "checked-item" : "not-checked-item";


  return (
    <section className="container">
      <div className="input-Filter">        
        <div className="search-input">
          <SearchBar onSubmit={getSearchData}/>
        </div>

        <div className="faculty">
          <div className="faculty-facultyList">
            <div className="faculty-title">Faculty</div>
            <div className="faculty-list-container">
              <Faculty onChange={getFacultyChecked}/>
            </div>
          </div>
        </div>

        <div className="domain">
          <div className="domain-domainList">
            <div className="domain-title">Domain</div>
            <div className="domain-list-container">
              {
                domainList.map((item, index) => (
                  <div key={index}>
                    <input value={item} type="checkbox" onChange={handledomainCheck} />
                    <span className={isdomainChecked(item)}>{item}</span>
                  </div>
                ))
              }
            </div>
          </div>
          <div>
            {`Items checked are: ${domaincheckedItems}`}
          </div>
        </div>
      </div>

      <div className='content'>
        <div className='videoContent'>
          <Video/>
        </div>
        <div className='videoContent'>
          <VideoContent/>
        </div>
          
      </div>
    </section>
  )
}

export default Content
