import React from "react";
import { useLocation } from "react-router-dom";
import { useEffect } from "react";
import ReactPlayer from 'react-player';
import { useRef } from "react";

function VideoDetails() {
    let location = useLocation();
    let video_url = location.state;

    video_url = video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)

    const p = React.useRef();

    useEffect(() => {
        this.p.seekTo(18.7);
    }, []);
    
    return (
        <div className="App">
          <ReactPlayer
            ref={(p) => { this.p = p}}
            url={video_url}
            className="react-player"
            playing
            controls
            width="100%"
            height="100%"
          />
          
          <button onClick={() => this.p.seekTo(0.9999999)}>Seek to end</button>
          <button onClick={() => this.p.seekTo(0.999)}>Seek to end</button>
          <button onClick={() => this.p.seekTo(12.7)}>Seek to 12.7</button>
          <button onClick={() => this.p.seekTo(42.65)}>Seek to 42.65</button>
        </div>
    );
}

export default VideoDetails











