import { useState, useEffect } from "react"
import axios from "axios"

const Video = (props) => {
  const [myData, setMyData] = useState([])
  const [isError, setIsError] = useState("")

  const searchData = props.searchData;
  const faculty = props.faculty;
  const domain = props.domain;
  const video_url = props.video_url;

  const getMyPostData = async () => {
    try {
      const res = await axios.get("/posts")
      setMyData(res.data)
    } catch (error) {
      setIsError(error.message)
    }
  }

  useEffect(() => {
    axios.get('./data.json')
      .then((res) => setMyData(res.data)
    )
  })

  return (
    <div className="video-grid">
      {
        myData.map((post) => {
          let { title, domain, faculty, video_url } = post

          if((title.includes(searchData)) || (faculty.includes(searchData)) || (domain.includes(searchData))){
            return (
              <div className="videoContent">
                <video width="500" height="290" controls>
                  <source type="video/mp4" src={video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)}></source>
                </video>
              </div>
            )
          } 
        })
      }   
    </div>    
  )
}

export default Video
