import axios from "axios";
import React, { useState, useEffect} from "react";
import ReactPlayer from "react-player";

function VideoDetails() {
  const [myData, setMyData] = useState([])
  const [searchKeyword, setSearchKeyword] = useState("");
  const [instance, setInstance] = useState(null);

  let localtitle = localStorage.getItem('title')
  let localfaculty = localStorage.getItem('faculty')
  let localdomain = localStorage.getItem('domain')
  let video_url = localStorage.getItem('videoUrl')

  video_url = video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)
  
  const handleStart = (startTime) => {
    if(instance) {
      if(isNaN(startTime)) {
        startTime = "0.0";
      }
      instance.seekTo(startTime);
    }
  };

  useEffect(() => {
    axios.get('./india.json')
      .then((res) => setMyData(res.data)
    )
  })

  const display = (startTime) => {
    startTime = startTime.slice(1,startTime.length-1)
    startTime = startTime.split(':')

    let hr = Number(startTime[0])
    let min = Number(startTime[1])
    let sec = Number(startTime[2])

    startTime = hr*60*60+min*60+sec

    const btn = document.createElement("button");
    btn.innerHTML = "Seek to " + startTime;
    btn.id = 'seekToBtn'
    btn.onclick = handleStart.bind(this,startTime)
    document.body.appendChild(btn);
  }

  const handleChange = (e) => {
    let searchData = e.target.value
    searchData = searchData.toUpperCase()
    setSearchKeyword(searchData);
  };

  return (
    <div className="videoDetails">
        <div className="videoDetails-video">
            <ReactPlayer
                ref={setInstance}
                url={video_url}
                playing
                controls
                onStart={handleStart}
            />
        </div>

        <div className="videoDetails-btn">
            <input type='text' id='searchKeyword' onChange={handleChange}></input>
        </div>   
        
        <div className="videoDetails-data">
            {
                myData.map((post) => {
                    //console.log("Hello")
                    let { title, faculty, domain, keyframes_text } = post

                    title = title.toUpperCase()
                    faculty = faculty.toUpperCase()
                    domain = domain.toUpperCase()

                    localtitle = localtitle.toUpperCase()
                    localdomain = localdomain.toUpperCase()
                    localfaculty = localfaculty.toUpperCase()

                    if((title === localtitle) || (domain === localdomain) || (faculty === localfaculty)){
                        let len = Object.keys(keyframes_text).length
                        
                        for(let i=1;i<=len;i++){
                            let obj = keyframes_text[i]
                            let startTime = obj[0]
                            let endTime = obj[1]
                            let keywords= obj[2].slice(1,obj[2].length-1).split(',')

                            keywords.forEach(function(key,j) {
                                if(searchKeyword === '')
                                    return
                                if(key.includes(searchKeyword)) {
                                    return (<div> {display(startTime)} </div>)
                                }
                            })
                        }
                    }
                })
            }
        </div> 
    </div>
  );
}

export default VideoDetails;





