import axios from "axios"
import React, { useState, useEffect } from "react"
import VideoDetails from "../VideoDetails"

function List(props) {
  let searchData = props.searchData
  let jsonPath
  const [myData, setMyData] = useState([])
  const [myJsonData, setJsonData] = useState([])
  const [myAlllabel, setAllLabels] = useState(true)
  const [mylabel, setLabels] = useState(null)

  if ("INDIA".includes(searchData)) {
    jsonPath = "./india-cluster.json"
  } else if ("PHYSICS".includes(searchData)) {
    jsonPath = "./physics-cluster.json"
  } else if ("LAW".includes(searchData)) {
    jsonPath = "./law-cluster.json"
  }

  useEffect(() => {
    axios.get(jsonPath).then((res) => setMyData(res.data))
  }, [searchData])

  useEffect(() => {
    axios.get('./india.json').then((res) => setJsonData(res.data))
  })

  const handleListClick = (labels, documents) => {
    setAllLabels(false)
    setLabels(labels)
  }

  const linkStyle = {
    color: 'black',
    fontSize: '18px'
  };

  const handleClick = (video_url) => {
    localStorage.setItem('title', title);
    localStorage.setItem('faculty', faculty);
    localStorage.setItem('domain', domain);
    localStorage.setItem('videoUrl', video_url);
  }

  const displayContent = (title,transcript,videoFileName) => {
    return (
      <div>
        <h4 id="chart-title">{title}</h4>
        <p>{transcript}</p>
        <button id='view-VideoDetails' onClick={handleClick.bind(this,video_url)}>
          <Link to="/videoDetails" style={linkStyle} target="_blank">Video Details</Link>
        </button>
        <VideoContent title={title} transcript={transcript} videoFileName={videoFileName}/>   
      </div>
    )
  }

  return (
    <div className="displayList">
      <div className="list-content" id="listView">
        {myData.map((post) => {
          let { labels, documents } = post

          labels = labels[0]

          return (
            <div>
              <div>
                <button id="list-btn" onClick={handleListClick.bind(this, labels, documents)}>
                  <h4>{labels}</h4>
                </button>
              </div>
            </div>
          )
        })}
      </div>

      {myAlllabel && (
        <div className="list-content" id="list-content">
          <div>
            {myData.map((post) => {
              let { labels, documents } = post

              labels = labels[0]

              return documents.map((doc, i) => {
                let title = doc.title
                //let content = doc.content
                let transcript = doc.transcript
                let videoFileName = './rbi/' + doc.video_File_Name + '.mp4'
                let uniqueId = Math.floor((Math.random() * 50) + 1)
                
                return (<div key={i}> {displayContent(title,transcript,videoFileName)} </div>)
              })
            })}
          </div>
        </div>
      )}

      {mylabel && (
        <div className="list-content" id="list-content">
          <div>
            {myData.map((post) => {
              let { labels, documents } = post

              labels = labels[0]

              if(mylabel === labels) {
                return documents.map((doc, i) => {
                  let title = doc.title
                  //let content = doc.content
                  let transcript = doc.transcript
                  let videoFileName = './rbi/' + doc.video_File_Name + '.mp4'
                  let uniqueId = Math.floor((Math.random() * 50) + 1)
                
                  return (<div key={i}> {displayContent(title,transcript,videoFileName)} </div>)
                })
              }
            })}
          </div>
        </div>
      )}
    </div>
  )
}

export default List
