import axios from "axios";
import React, { useState, useEffect} from "react";
import ReactPlayer from "react-player";
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Popover from 'react-bootstrap/Popover';

function VideoDetails() {
  const [myData, setMyData] = useState([])
  const [searchKeyword, setSearchKeyword] = useState(0);
  const [instance, setInstance] = useState(0);

  let localtitle = localStorage.getItem('title')
  let localfaculty = localStorage.getItem('faculty')
  let localdomain = localStorage.getItem('domain')
  let video_url = localStorage.getItem('videoUrl')

  video_url = video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)
  
  const handleStart = (startTime) => {
    if(instance) {
      if(isNaN(startTime)) {
        startTime = "0.0";
      }
      instance.seekTo(startTime);
    }
  };

  useEffect(() => {
    axios.get('./india.json')
      .then((res) => setMyData(res.data)
    )
  }, []);

  const myDiv = document.createElement('div');
  myDiv.className = 'keywords-container'

  const displayVid = (orginaltimeStamp,elemenID) => {
    myDiv.innerHTML = ''
    const timeStamp = new Set(orginaltimeStamp);

    const btnContainer = document.createElement('div')
    
    for(let startTime of timeStamp.values()){
        let originalTime = startTime
        startTime = startTime.split(':')

        let hr = parseInt(startTime[0], 10);
        let min = parseInt(startTime[1], 10);
        let sec = parseInt(startTime[2], 10);

        startTime = (hr * 3600) + (min * 60) + sec
        
        const myButton = document.createElement('button');
        myButton.innerHTML = 'Seek to ' + originalTime;
        myButton.id = 'seekToBtn';
        myButton.onclick = handleStart.bind(this,startTime)
        myDiv.append(myButton);

        btnContainer.append(myDiv)

        document.body.append(myDiv);
    }
    //try to pop open pop window
    //document.body.innerHTML = ''
  }

  let [keyword, setKeyword] = useState('');

  const onKeywordChange = (e) => {
    let selectedkeyword = e.target.value
    selectedkeyword = selectedkeyword.toUpperCase()

    if(selectedkeyword === "Default")
        selectedkeyword = ''
    setSearchKeyword(selectedkeyword)
  }

  let allKeywords = []

  return (
    <div className="videoDetails">
        <div className="videoDetails-video">
            <ReactPlayer
                ref={setInstance}
                url={video_url}
                playing
                controls
                onStart={handleStart}
            />
        </div> 
        
        <div className="videoDetails-data">
            {
                myData.map((post) => {
                    let { title, faculty, domain, keyframes_text } = post

                    title = title.toUpperCase()
                    faculty = faculty.toUpperCase()
                    domain = domain.toUpperCase()
                    localtitle = localtitle.toUpperCase()
                    localfaculty = localfaculty.toUpperCase()
                    localdomain = localdomain.toUpperCase() 

                    if((title === localtitle)){
                        let len = Object.keys(keyframes_text).length
                        let timeStamp = [];

                        for(let i=1;i<=len;i++){
                            let obj = keyframes_text[i]
                            let startTime = obj[0]
                            let endTime = obj[1]
                            let keywords= obj[2].slice(1,obj[2].length-1).split(',')

                            keywords.forEach(function(key,j) {
                                let slicedKey = key.slice(2,key.length-1)
                                allKeywords.push(slicedKey)
                                key = key.toUpperCase()

                                if(searchKeyword === '')
                                    return
                                if(key.includes(searchKeyword))
                                    timeStamp.push(startTime)
                            })
                        }
                        
                        //return (<div>{displayVid(timeStamp,'btnContainer')}</div>)

                        return (
                          <div>
                            <OverlayTrigger
                              trigger="click" rootClose placement="top"
                              overlay={
                                <Popover id={`displayTimeStamp`} style={{height:"250px",width:"400px"}}>
                                  <Popover.Header as="h3">
                                    Transcript
                                    <button id='closeTimeStamp'><i class="fa fa-close"></i></button>
                                  </Popover.Header>
                                  <Popover.Body style={{height:"205px",width:"290px", overflow:"hidden"}}>
                                    <div style={{overflowY:"scroll",height:"200px"}}>
                                      hello
                                    </div>
                                  </Popover.Body>
                                </Popover>
                              }
                            > 
                            <button id='viewTranscipt-btn'>Click here</button>
                            </OverlayTrigger>
                          </div>
                        )
                    }
                })
            }
        </div>

        <div className="videoDetails-btn">
            <div className="keyword-container">
            <select id='select-keyword' onChange={onKeywordChange}>
              <option></option>
                {
                  allKeywords.map((option, index) => {
                    return <option key={index} > {option} </option>
                  })
                }
            </select>
        </div>
        </div>   
    </div>
  );
}

export default VideoDetails;




