function List(props) {
  let searchData = props.searchData
  let jsonPath, labelCount=0, allLabelCount=0
  const [myData, setMyData] = useState([])
  const [myAlllabel, setAllLabels] = useState(true)
  const [mylabel, setLabels] = useState(null)
  const [selectedTopic, setSelectedTopic] = useState(null)
  const [allIndiaData, setallIndiaData] = useState([])

  if ("INDIA".includes(searchData)) {
    jsonPath = "./india-cluster.json"
  } else if ("PHYSICS".includes(searchData)) {
    jsonPath = "./physics-cluster.json"
  } else if ("LAW".includes(searchData)) {
    jsonPath = "./law-cluster.json"
  }

  useEffect(() => {
    axios.get(jsonPath).then((res) => setMyData(res.data))
  }, [searchData])

  useEffect(() => {
    axios.get('./allIndiaData.json').then((res) => {setallIndiaData(res.data);console.log(res.data)})
  }, [searchData])

  const [activeButton, setActiveButton] = useState(null);
  const [facultySelected, setFaculty] = useState();
  const [subjectSelected, setSubject] = useState();
  
  const handleListClick = (event, labels, documents) => {
    setFaculty('')
    setSubject('')
    setSelectedTopic(labels)
    var currentButton = event.target;
    currentButton.style.backgroundColor = "grey";
    currentButton.style.border = "2px solid black";
    currentButton.style.height = "2rem"
    currentButton.style.width = "10rem"

    if(activeButton && activeButton !== currentButton) {
      activeButton.style.backgroundColor = "";
      activeButton.style.border = "";
      activeButton.style.height = ""
      activeButton.style.width = ""
    }

    setActiveButton(currentButton);
    setAllLabels(false);
    setLabels(labels);
  };

  const closeTranscript = () => {
    document.querySelector('#displayTranscript').style.display = 'none'
  }

  const closeFilterBtn = () => {
    document.querySelector('#displayFilter').style.display = 'none'
  }

  const renderDescriptionOverlay = (content) => {
    return (
      <OverlayTrigger
        trigger="click" rootClose placement="top"
        overlay={
          <Popover id={`displayTranscript`} style={{height:"260px",width:"400px",zIndex:0}}>
            <Popover.Header as="h3">
              Description
              <button id='closeTranscript' onClick={closeTranscript.bind(this)}><i class="fa fa-close"></i></button>
            </Popover.Header>
            <Popover.Body style={{height:"205px",width:"290px", overflow:"hidden"}}>
              <div style={{overflowY:"scroll",height:"200px"}}>{content}</div>
            </Popover.Body>
          </Popover>
        }
      > 
        <button id='descriptionBtn'>Description</button>
      </OverlayTrigger>
    )
  }

  const displayContent = (title,content,transcript,videoFileName) => {
    return (
      <div className="displaylistContent">
        <p id="list-title">{title}</p>
        <video src={videoFileName} controls height="360px" width="100%"></video>
        <button id='view-VideoDetails' onClick={() => handleshowDiv(title, videoFileName)}>Watch Video</button>
        {renderDescriptionOverlay(content)}
      </div>
    )
  }

  const [showDiv, setShowDiv] = useState(false);
  const [myTitle, setTitle] = useState();
  const [myVideoFileName, setVideoFileName] = useState();

  const handleshowDiv = (title,videoFileName) => {
    setShowDiv(true)
    setTitle(title)
    setVideoFileName(videoFileName)
  };

  const handlehideDiv = () => {
    setShowDiv(false)
    setTitle()
    setVideoFileName()
  }

  let allFacultyName = [], allSubjects = [];

  const onFacultyChange = () => {
    let selectedTeacher = document.getElementById("select-faculty").value;
    
    if(selectedTeacher === "Default") 
      selectedTeacher = "";
    
    setFaculty(selectedTeacher)
  };

  const onSubjectChange = () => {
    let selectedSubject = document.getElementById("select-subject").value;
    
    if(selectedSubject === "Default") 
      selectedSubject = "";
    
    setSubject(selectedSubject)
  };

  const renderFilterOverlay = () => {
    return (
      <OverlayTrigger
        trigger="click"
        rootClose
        placement="bottom"
        overlay={
          <Popover id={`displayFilter`} style={{ height: "260px", width: "400px", zIndex: 0 }}>
            <Popover.Header as="h3">
              Filter
              <button id='closeFilterBtn' onClick={closeFilterBtn.bind(this)}><i className="fa fa-close"></i></button>
            </Popover.Header>
            <Popover.Body style={{ height: "205px", width: "290px", overflow: "hidden" }}>
              <div style={{ overflowY: "scroll", height: "200px" }}>
                <div id='facultyFilter'>
                  Faculty
                  <select id="select-faculty" onChange={onFacultyChange} >
                    <option>Default</option>
                    {allFacultyName.map((option, index) => {
                      return <option key={index}> {option} </option>;
                    })}
                  </select>
                </div>
                <div id='subjectFilter'>
                  Subject
                  <select id="select-subject" onChange={onSubjectChange}>
                    <option>Default</option>
                    {allSubjects.map((option, index) => {
                      return <option key={index}> {option} </option>;
                    })}
                  </select>
                </div>
              </div>
            </Popover.Body>
          </Popover>
        }
      >
        <button id='filterBtn'>Filter</button>
      </OverlayTrigger>
    );
  }
  
  return (
    <div className="displayList">

      {showDiv && (
        <div id="overlay">
          <button id='closeBtn' onClick={handlehideDiv}><i class="fa fa-times" aria-hidden="true"></i></button>
          <VideoDetails myTitle={myTitle} myVideoFileName={myVideoFileName} showDiv={showDiv}/>
        </div>
      )}

      <div>
        {allIndiaData.map((post) => {
          let { domain, faculty } = post
      
          documents.map((doc) => {
            allSubjects.push(domain)
            allFacultyName.push(faculty)
          })
        })}
      </div>

      <div className="list-content" id="listView">
        {myData.map((post) => {
          let { labels, documents } = post

          labels = labels[0]

          for(let i=0;i<documents.length;i++){
            if(mylabel === labels) 
              labelCount++
            allLabelCount++
          }

          

          return (
            <div className="displayLabelList">
                <button id="list-btn" onClick={(event) => handleListClick(event, labels, documents)}>
                  <p id='displaylabel'>{labels}</p>
                </button>
            </div>
          )
        })}
      </div>

      {myAlllabel && (
        <div className="list-content" id="list-content">
          <div className="label-count">
            <div id='labelCount'>
              All retrieved results: {allLabelCount}
              {renderFilterOverlay()}
            </div> 
          </div>
          <div>
            {myData.map((post) => {
              let { labels, documents } = post

              labels = labels[0]

              return documents.map((doc, i) => {
                let title = doc.title
                let content = doc.content
                let domain = doc.domain
                let faculty = doc.faculty
                let transcript = doc.transcript
                //let videoFileName = './' + doc.title + '/' + doc.title + '.mp4'
                let videoFileName = './ImgVideoData' + '/video/' + doc.title + '.mp4'
                //let videoFileName = './' + title + '/' + title + '.mp4'
                //console.log(videoFileName)

                if((facultySelected === faculty || subjectSelected === domain))
                  return (<div key={i}> {displayContent(title,content,transcript,videoFileName)} </div>)
                else if(facultySelected === '' && subjectSelected === '')
                  return (<div key={i}> {displayContent(title,content,transcript,videoFileName)} </div>)
              })
            })}
          </div>
        </div>
      )}

      {mylabel && (
        <div className="list-content" id="list-content">
          <div className="label-count">
            <div id='labelCount'>
              All retrieved results for topic {selectedTopic}: {labelCount}
              {renderFilterOverlay()}
            </div> 
          </div>
          <div>
            {myData.map((post) => {
              let { labels, documents } = post

              labels = labels[0]

              if(mylabel === labels) {
                return documents.map((doc, i) => {
                  let title = doc.title
                  let content = doc.content
                  let domain = doc.domain
                  let faculty = doc.faculty
                  let transcript = doc.transcript
                  //let videoFileName = './Reserve bank of India/' + doc.title + '.mp4'
                  //let videoFileName = './' + doc.title + '/' + doc.title + '.mp4'
                  let videoFileName = './ImgVideoData' + '/video/' + doc.title + '.mp4'
                  //console.log(videoFileName)

                  if((facultySelected === faculty || subjectSelected === domain))
                    return (<div key={i}> {displayContent(title,content,transcript,videoFileName)} </div>)
                  else if(facultySelected === '' && subjectSelected === '')
                    return (<div key={i}> {displayContent(title,content,transcript,videoFileName)} </div>)
                })
              }
            })}
          </div>
        </div>
      )}
    </div>
  )
}

export default List









