import axios from "axios";
import React, { useState, useEffect } from "react";
import { PieChart, Pie, Cell, Legend } from 'recharts';

const India = () => {
    const [data, setMyData] = useState([])
    const [selectedOuterData, setSelectedOuterData] = useState(null)
    const COLORS = ["#FF6384","#36A2EB","#FFCE56","#1abc9c","#3498db","#9b59b6","#34495e","#f1c40f","#e74c3c","#95a5a6","#2ecc71","#8e44ad","#d35400","#7f8c8d","#27ae60"];

    const handleOuterPieEnter = (data) => {
        setSelectedOuterData(data);
    };

    const [chartData, setChartData] = useState([])

    useEffect(() => {
      axios.get('./india-cluster.json')
        .then((res) => setMyData(res.data))
    }, []);

    useEffect(() => {
      let newChartData = []
      data.forEach((post) => {
        let { labels, documents } = post
        for(let i=0;i<documents.length;i++){
          let title = documents[i].title
          let titleCount = 1
          newChartData.push({ title: title, count: titleCount })
        }
      })
      setChartData(newChartData)
    }, [data]);

    return (
        <div className="clusters">
            <div className="displayPieChart">
                <div className="piechart-content">
                    <PieChart width={400} height={400}>
                        <Legend />
                        <Pie
                            data={chartData}
                            dataKey="count"
                            nameKey="title"
                            cx="50%"
                            cy="50%"
                            outerRadius={80}
                            innerRadius={40}
                            fill="#8884d8"
                            label
                            onClick={handleOuterPieEnter}
                        >
                            {
                                chartData.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                ))
                            }
                        </Pie>
                    </PieChart>
                </div>

                <div className="piechart-content">   
                    <h2>{selectedOuterData && selectedOuterData.title}</h2> 
                    {
                        data.map((post) => {
                            let { documents } = post
                            
                            for(let i=0;i<documents.length;i++){
                                let title = documents[i].title
                                if(title === selectedOuterData.title) {
                                    return (
                                        <div>
                                            <p key={i}>{documents[i].content}</p>
                                        </div>
                                    )
                                }
                            }
                        })
                    }
                </div>
            </div>
        </div>
    )  
}

export default India
