import React from 'react';
import { Pie } from 'react-chartjs-2';

const data = [
  {
    "labels": [
      "India"
    ],
    "label_counts": 6,
    "documents": [
      {
        "title": "Reserve bank of India",
        "content": "bank, rate, rbi, commercial, reserve, repo, borrowing, money, ratio, current, cash, india, make, interest, currency, deposit, time, policy, le, charged"
      },
      {
        "title": "Climate and wildlife",
        "content": "forest, found, india, season, vegetation, monsoon, wildlife, tree, type, weather, bengal, climate, hot, many, natural, region, animal, bird, mangrove, day"
      },
      {
        "title": "Judiciary",
        "content": "court, judiciary, judicial, state, review, india, laxman, high, law, supreme, sudha, power, right, fundamental, independent, constitution, structure, term, kumar, citizen"
      },
    ]
  },
  {
    "labels": [
      "Bengal"
    ],
    "label_counts": 3,
    "documents": [
      {
        "title": "Indian National movement",
        "content": "indian, thank, british, bengal, badruddin, leader, people, social, tyabji, chandra, n, movement, congress, inc, partition, gokhale, dadabhai, national, tilak, roy"
      },
      {
        "title": "Climate and wildlife",
        "content": "forest, found, india, season, vegetation, monsoon, wildlife, tree, type, weather, bengal, climate, hot, many, natural, region, animal, bird, mangrove, day"
      }
    ]
  }
];

const getPieChartData = () => {
  const titles = data.reduce((allTitles, labelData) => {
    return allTitles.concat(labelData.documents.map(doc => doc.title));
  }, []);
  const titleCounts = titles.reduce((titleCounts, title) => {
    titleCounts[title] = (titleCounts[title] || 0) + 1;
    return titleCounts;
  }, {});
  const titleLabels = Object.keys(titleCounts);
  const titleData = titleLabels.map(title => titleCounts[title]);
  return {
    labels: titleLabels,
    datasets: [
      {
        data: titleData,
        backgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56',
          '#8B008B',
          '#FF00FF',
          '#9400D3',
          '#0000CD',
          '#00008B',
          '#00BFFF',
          '#00CED1'
        ]
      }
    ]
  };
};

const IndiaCluster = () => {
  return (
    <Pie data={getPieChartData()} />
  );
};

export default IndiaCluster
