import React from "react";
import ReactPlayer from "react-player";
import { useState, useCallback } from "react";
import { useLocation } from "react-router-dom";

const [instance, setInstance] = useState(null);
   
const handleStart = useCallback(() => {
    if (instance) {
        instance.seekTo(timePlayed)
    }
}, [instance, timePlayed])

const handleClick = ( url, onEnded, timePlayed, playing) => {
    
   
    return (
       <div className="video-player">
           <ReactPlayer
               ref={setInstance}
               url={url}
               playing={playing}
               controls={true}
               onStart={handleStart}
               onEnded={onEnded}
           />
       </div>
    )
};

function VideoDetails() {
    let location = useLocation();
    let video_url = location.state;

    video_url = video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)

    return (
        <div>
            <button onClick={handleClick( video_url, 19, 10, "playing")}>10sec</button>
            <button onClick={handleClick( video_url, 35, 20, "playing")}>20sec</button>
        </div>
    )
}

export default VideoDetails










