import React, { useState, useEffect } from 'react'
import SearchBar from './SubComponents/SearchBar'
import Video from './SubComponents/Video'
import VideoContent from './SubComponents/VideoContent'
import axios from "axios"

function Content() {
  const [searchData, setSearchData] = useState('');
  const [faculty, setFaculty] = useState('');
  const [domain, setDomain] = useState('');

  const facultyList = ["Kohli", "David", "Sharma", "Singh", "Chopra"];
  const domainList = ["Social", "Finance", "History", "Physics", "Chemistry", "Mathematics"];

  const getSearchData = (data) => {
    setSearchData(data);
  };

  const onFacultyChangeHandler = (event) => {
    setFaculty(event.target.value)
  }

  const onDomainChangeHandler = (event) => {
    setDomain(event.target.value)
  }

  const [myData, setMyData] = useState([])
  const [isError, setIsError] = useState("")
  
  const getMyPostData = async () => {
    try {
      const res = await axios.get("/posts")
      setMyData(res.data)
    } catch (error) {
      setIsError(error.message)
    }
  }

  useEffect(() => {
    axios.get('./data.json')
      .then((res) => setMyData(res.data)
    )
  })

  const displayfacultyDomain = function(searchData) {
    return (
      <div className='facultyDomain'>
        <div className="faculty-container">
          <div className='faculty-title'>Faculty</div>
          <select onChange={onFacultyChangeHandler}>
            <option>Select faculty</option>
              {
                facultyList.map((option, index) => {
                  return <option key={index} > {option} </option>
                })
              }
          </select>
        </div>

        <div className="domain-container">
        <div className='domain-title'>Domain</div>
          <select onChange={onDomainChangeHandler}>
            <option>Select domain</option>
              {
                domainList.map((option, index) => {
                  return <option key={index} > {option} </option>
                })
              }
          </select>
        </div>
      </div>
    )
  }

  return (
    <>
      <div className="container">
        <div className="searchFilter">  
          <div className='searchBar'>
            <SearchBar onSubmit={getSearchData}/>   
          </div> 

          <div>
            {
              myData.map((post) => {
                if(searchData == ''){
                  return
                }
                else{
                  return (<div> {displayfacultyDomain(searchData)} </div>)

                  
                }
              })
            }
          </div>          
        </div>
      </div>
    </>
  )
}

export default Content
