import React from "react";
import { useState } from "react";

const Pagination = ({ currentPage, postsPerPage, totalPosts, paginate, countOfVideos }) => {
  const pageNumbers = [];
  const [activePage, setActivePage] = useState(1);

  for(let i=1; i<=(countOfVideos/3); i++) {
    pageNumbers.push(i);
  }

  return (
    <nav>
      <div className="pagination">
        {pageNumbers.map((number) => (
          <p key={number} className="page-item">
            <a id='pagination-btn' onClick={() => {paginate(number); setActivePage(number)}} 
                className={`page-link ${number === activePage ? "active" : ""}`
              }
            >{number}</a>
          </p>
        ))}
      </div>
    </nav>
  );
};

export default Pagination;



