import { useState } from 'react'

function Content() {
  //Search bar
  const [searchData, setSearchData] = useState('');
  
  const handleSearchChange = (event) => {
    setSearchData(event.target.value);
  };






  //Faculty
  const [facultychecked, setFacultyChecked] = useState([]);
  const facultyList = ["Kohli", "David", "Sharma", "Singh", "Chopra"];

  const handlefacultyCheck = (event) => {
    var updatedfacultyList = [...facultychecked];

    if(event.target.checked) {
      updatedfacultyList = [...facultychecked, event.target.value];
    } 
    else {
      updatedfacultyList.splice(facultychecked.indexOf(event.target.value), 1);
    }
    setFacultyChecked(updatedfacultyList);
  };

  const facultycheckedItems = facultychecked.length 
    ? facultychecked.reduce((total, item) => {
        return total + ", " + item;
      })
    : "";

  const isfacultyChecked = (item) =>
    facultychecked.includes(item) ? "checked-item" : "not-checked-item";

  






  //Domain
  const [checked, setDomainChecked] = useState([]);
  const domainList = ["Kohli", "David", "Sharma", "Singh", "Chopra"];

  const handledomainCheck = (event) => {
    var updatedfacultyList = [...checked];

    if(event.target.checked) {
      updatedfacultyList = [...checked, event.target.value];
    } 
    else {
      updatedfacultyList.splice(checked.indexOf(event.target.value), 1);
    }
    setDomainChecked(updatedfacultyList);
  };

  const domaincheckedItems = checked.length 
    ? checked.reduce((total, item) => {
        return total + ", " + item;
      })
    : "";

  const isdomainChecked = (item) =>
    checked.includes(item) ? "checked-item" : "not-checked-item";



  return (
    <section class="content">
      <div class="content-container">
        
        <div class="search-input">
          <input type="text" id="searchData" name="searchData" placeholder="Search" onChange={handleSearchChange}/>
          <h2>Search: {searchData}</h2>
        </div>

        


        <div className="faculty">
          <div className="faculty-facultyList">
            <div className="faculty-title">Faculty</div>
            <div className="faculty-list-container">
              {
                facultyList.map((item, index) => (
                  <div key={index}>
                    <input value={item} type="checkbox" onChange={handlefacultyCheck} />
                    <span className={isfacultyChecked(item)}>{item}</span>
                  </div>
                ))
              }
            </div>
          </div>

          <div>
            {`Items checked are: ${facultycheckedItems}`}
          </div>
        </div>







        <div className="domain">
          <div className="domain-domainList">
            <div className="domain-title">Faculty</div>
            <div className="domain-list-container">
              {
                domainList.map((item, index) => (
                  <div key={index}>
                    <input value={item} type="checkbox" onChange={handledomainCheck} />
                    <span className={isdomainChecked(item)}>{item}</span>
                  </div>
                ))
              }
            </div>
          </div>

          <div>
            {`Items checked are: ${domaincheckedItems}`}
          </div>
        </div>
      </div>
    </section>
  )
}

export default Content
