import React from 'react'
import { Link } from "react-router-dom";

const Video = (props) => {
  const title = props.title;
  const faculty = props.faculty;
  const domain = props.domain;
  const video_url  = props.video_url;

  const linkStyle = {
    color: 'black',
    fontSize: '18px'
  };

  const handleClick = (video_url) => {
    localStorage.setItem('videoUrl', video_url);
  }

  return (
    <div className="videoContent">
      <div className="videoUpperContent">
        <video width="558" height="290" controls>
          <source type="video/mp4" src={video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)}></source>
        </video>
      </div>

      <div className="videoLowerContent">
        <button id='view-VideoDetails' onClick={handleClick.bind(this,video_url)}>
          <Link to="/videoDetails" style={linkStyle} target="_blank">Video Details</Link>
        </button>
      </div>  
    </div>
  )
}

export default Video


