import axios from "axios";
import React, { useState, useEffect } from "react";
import SearchBar from "./SubComponents/SearchBar";
import Video from "./SubComponents/Video";
import VideoContent from "./SubComponents/VideoContent";
import Charts from "./SubComponents/PieChart/Charts";
import List from "./SubComponents/PieChart/List";

const Content = () => {
  const [myData, setMyData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(2);
  let [searchData, setSearchData] = useState("");

  const filteredData = myData.filter((item) => {
    return item.title.toUpperCase().includes(searchData.toUpperCase());
  });

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredData.slice(indexOfFirstItem, indexOfLastItem);

  useEffect(() => {
    axios.get("data.json").then((res) => {
      setMyData(res.data);
    });
  }, []);

  const handleClick = (event) => {
    setCurrentPage(Number(event.target.id));
  };

  const pageNumbers = [];
  
  for(let i=1; i<=(Math.ceil(filteredData.length / itemsPerPage)); i++) {
    pageNumbers.push(i);
  }

  let [teacher, setTeacher] = useState("");
  let [domains, setDomains] = useState("");

  let teacherList = [];
  let domainsList = [];

  const getSearchData = (data) => {
    data = data.toLowerCase();
    setSearchData(data);
    setCurrentPage(1);
  };

  if (("chemistry" || "chemistry - pesticides").includes(searchData)) {
    teacherList = ["Kalaam"];
    domainsList = ["Chemistry"];
  } 
  else if (("social" || "law and social justice").includes(searchData)) {
    teacherList = ["Singh"];
    domainsList = ["Social"];
  } 
  else if ("history".includes(searchData)) {
    teacherList = ["Kalaam"];
    domainsList = ["History"];
  } 
  else if (("finance" || "reserve bank").includes(searchData)) {
    teacherList = ["Singh"];
    domainsList = ["Finance"];
  } 
  else if(("history" || "judiciary" || "constitution").includes(searchData)) {
    teacherList = ["David"];
    domainsList = ["Constitution"];
  } 
  else if (("mathematics" || "matrix" || "nin-max normalization").includes(searchData)) {
    teacherList = ["Kohli"];
    domainsList = ["Mathematics"];
  } 
  else if (("physics" || "physics - magnetism" || "physics - raman spectroscopy").includes(searchData)) {
    teacherList = ["Singh", "David"];
    domainsList = ["Physics"];
  } 
  else if(('india' || 'india - judiciary' || 'indian - constitution' || 'reserve bank of india' || 'indian national movement' || 'indian desert' || 'india - climate,vegetation and wildlife').includes(searchData)){
    teacherList = ['Singh','David','Kalaam','Sharma']
    domainsList = ['History','Finance','Geography','Constitution']
  }
  else if(('geography' || 'indian desert' || 'minerals and power resources' || 'GEOGRAPHY - COASTAL PLAINS AND ISLANDS' || 'agriculture' || 'globe - latitudes and longitudes' || 'india - climate,vegetation and wildlife' || 'human resource').includes(searchData)){
    teacherList = ['Singh','David','Kalaam','Sharma','Chopra']
    domainsList = ['Geography']
  }
  else if(('computer science' || 'cyber security' || 'ranked retrieval' || 'MACHINE LEARNING').includes(searchData)){
    teacherList = ['Kohli','Sharma','Chopra']
    domainsList = ['Computer Science']
  }

  const onTeacherChangeHandler = () => {
    let selectedTeacher = document.getElementById("select-faculty").value;
    if (selectedTeacher === "Default") selectedTeacher = "";
    setTeacher(selectedTeacher);
    setCurrentPage(1)
  };

  const onDomainsChangeHandler = () => {
    let selectedDomain = document.getElementById("select-domain").value;
    if (selectedDomain === "Default") selectedDomain = "";
    setDomains(selectedDomain);
    setCurrentPage(1)
  };

  let showChartList = false;
  let [showPieChartContent, setShowPieChartContent] = useState(false);
  let [showListContent, setShowListContent] = useState(false);

  const handleListContent = () => {
    setShowPieChartContent(false);
    setShowListContent(true);
  };

  const handlePieChartContent = () => {
    setShowListContent(false);
    setShowPieChartContent(true);
  };

  const displayPieChartData = (searchData) => {
    return (
      <div>
        <div className="PieChart">
          <Charts searchData={searchData} />
        </div>
      </div>
    );
  };

  const displayListData = (searchData) => {
    return (
      <div>
        <div className="list">
          <List searchData={searchData} />
        </div>
      </div>
    );
  };

  const shortTranscript = (transcript, maxSentences) => {
    let i = 0,
      count = 0;
    let newTranscript = "";

    while (i < transcript.length) {
      if (count === maxSentences) break;
      if (transcript[i] === ".") count++;
      newTranscript = newTranscript + transcript[i++];
    }

    return newTranscript;
  };

  const displayVideoContent = (
    video_url,
    title,
    duration,
    faculty,
    domain,
    newTranscript,
    transcript
  ) => {
    return (
      <div className="videos-content">
        <Video
          title={title}
          faculty={faculty}
          domain={domain}
          video_url={video_url}
        />
        <VideoContent
          title={title}
          duration={duration}
          faculty={faculty}
          transcript={transcript}
          newTranscript={newTranscript}
        />
      </div>
    );
  };

  return (
    <div className="container">
      <div className="searchFilter">
        <div className="searchBar">
          <SearchBar onSubmit={getSearchData} />
        </div>

        <div className="facultyDomain">
          <div className="faculty-title">Faculty</div>
          <div className="faculty-container">
            <select id="select-faculty" onChange={onTeacherChangeHandler}>
              <option>Default</option>
              {teacherList.map((option, index) => {
                return <option key={index}> {option} </option>;
              })}
            </select>
          </div>

          <div className="domain-title">Domain</div>
          <div className="domain-container">
            <select id="select-domain" onChange={onDomainsChangeHandler}>
              <option>Default</option>
              {domainsList.map((option, index) => {
                return <option key={index}> {option} </option>;
              })}
            </select>
          </div>
        </div>
      </div>

      <div className="grid">
        {currentItems.map((item) => {
          if (searchData !== "") {
            showChartList = true;
            document.querySelector(".facultyDomain").style.display = "grid";

            document
              .querySelector("#search-btn")
              .addEventListener("click", function () {
                document
                  .getElementById("select-faculty")
                  .getElementsByTagName("option")[0].selected = "selected";
                document
                  .getElementById("select-domain")
                  .getElementsByTagName("option")[0].selected = "selected";

                setTeacher("");
                setDomains("");
                setCurrentPage(1);
              });

            let {
              domain,
              title,
              duration,
              faculty,
              video_url,
              transcript
            } = item;

            searchData = searchData.toUpperCase();
            domain = domain.toUpperCase();
            domains = domains.toUpperCase();
            teacher = teacher.toUpperCase();
            faculty = faculty.toUpperCase();
            title = title.toUpperCase();

            let maxSentences = 5;
            let newTranscript = shortTranscript(transcript, maxSentences);

            if (
              title.includes(searchData) ||
              domain.includes(searchData) ||
              faculty.includes(searchData)
            ) {
              if(title.includes(searchData) || domain.includes(searchData) || faculty.includes(searchData)) {
                if((teacher === faculty || teacher === '') && (domain === domains || domains === '')) {
                  return (<div> {displayVideoContent(video_url,title,duration,faculty,domain,newTranscript,transcript)} </div>)
                }
              }
            }
          }
        })}
        <div>
          {pageNumbers.map((number) => (
            <button key={number} id={number} onClick={handleClick}>
              {number}
            </button>
          ))}
        </div>
      </div>

      {showChartList && (
        <div className="chartList">
          <button id="listBtn" onClick={handleListContent}>
            List
          </button>
          <button id="pieChartBtn" onClick={handlePieChartContent}>
            Pie Chart
          </button>

          {showPieChartContent && <div>{displayPieChartData(searchData)}</div>}
          {showListContent && <div>{displayListData(searchData)}</div>}
        </div>
      )}
    </div>
  );
};

export default Content;
