import { useEffect, useState } from "react";

function Video() {
  const [data, setData] = useState()

  const fetchJson = () => {
    fetch('./data.json')
      .then(response => {
        return response.json();
      })
      .then(data => {
        setData(data);
      })
      .catch((e) => {
        console.log(e.message);
      });
  }
  
  useEffect(() => {
    fetchJson()
  },[])

  let video_url = data?.video_url

  return(
    <>
      <div className='videoContent'>Video Url : {video_url}</div>
    </>
  )
}

export default Video;

