import axios from "axios";
import { useState, useEffect } from "react";
import { PieChart, Pie, Label, Tooltip, Cell } from "recharts";

const IndiaCluster = () => {
    const [myData, setMyData] = useState([])
    const [selectedData, setSelectedData] = useState(null);
    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#AF19FF', '#F2F2F2'];
    
    const handlePieEnter = (data) => {
        console.log(data)
        setSelectedData(data);
    };

    useEffect(() => {
        axios.get('./india-clusters.json')
          .then((res) => setMyData(res.data)
        )
    }, [0]);

    return (  
        <div className="clusters">
            <div>
                {
                    myData.map((post) => {
                        let { labels,documents} = post
                        
                        labels = labels[0]

                        for(let i=0;i<documents.length;i++){
                            let title = documents[i].title
                            let content = documents[i].content
                        }
                    })
                }
            </div> 

            <div className="displayPieChart">
                <PieChart width={550} height={500}>
                    <Pie 
                        data={myData} 
                        dataKey="label_counts" 
                        nameKey="labels" 
                        outerRadius={200} 
                        innerRadius={130} 
                        labelLine={false}
                        cx={280}
                        cy={250}
                        label={(entry) => entry.labels}
                        onMouseEnter={handlePieEnter}
                        
                        >
                        {
                            myData.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]}>
                                <Label 
                                position="inside" 
                                content={({ value }) => `${value} (${entry.labels})`}
                                />
                            </Cell>
                            ))
                        }
                    </Pie>
                    <Tooltip />
                </PieChart>
            
                {selectedData && (
                    <div>
                    <h3>{selectedData.labels}</h3>
                    <p>Count: {selectedData.label_counts}</p>
                    </div>
                )}
           

            </div>
            
        </div>
    )
}

export default IndiaCluster



