import { useLocation } from "react-router-dom";

function VideoDetails() {
    const location = useLocation();
    const video_url = location.state;

    return (
        <div className="videoDetails">
            <div className="videoDetails-video">
                <video width="558" height="290" controls>
                    <source type="video/mp4" src={video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)}></source>
                </video>

                <ReactPlayer
                    ref={p => { this.p = p }}
                    url={video_url}
                    className='react-player'
                    playing
                    controls
                    width='100%'
                    height='100%'
                />
                <button onClick={() => this.p.seekTo(0.9999999)}>Seek to end</button>
                <button onClick={() => this.p.seekTo(0.999)}>Seek to end (works in Safari)</button>
                <button onClick={() => {
                    console.log(this.p.getDuration());this.p.seekTo(this.p.getDuration())}}>Seek to end (with getDuration())
                </button>
                <button onClick={() => this.p.seekTo(12.7)}>Seek to 12.7</button>
                <button onClick={() => this.p.seekTo(42.65)}>Seek to 42.65</button>
            </div>
        </div>
    )
}

export default VideoDetails
