import axios from "axios";
import React, { useState, useEffect } from "react";
import { PieChart, Pie, Cell, Legend } from 'recharts';

const India = () => {
    const [data, setMyData] = useState([])
    const [selectedInnerData, setSelectedInnerData] = useState(null)
    const COLORS = ["#FF6384","#36A2EB","#FFCE56","#1abc9c","#3498db","#9b59b6","#34495e","#f1c40f","#e74c3c","#95a5a6","#2ecc71","#8e44ad","#d35400","#7f8c8d","#27ae60"];

    const handleInnerPieEnter = (data) => {
        setSelectedInnerData(data);
    };

    useEffect(() => {
      axios.get('./india-cluster.json')
        .then((res) => setMyData(res.data)
      )
    }, [0]);

    const titles = data.reduce((item, { documents }) => {
        documents.forEach(({ title }) => {
          if (item[title]) {
            item[title]++;  //title already exists, increment it's count by 1
          } else {
            item[title] = 1;  //title does not exist, add it to the object(item) with count 1
          }
        });
        return item;
      }, {});

    const chartData = [];
        Object.keys(titles).forEach(title => {
            const count = titles[title];

            for(let i=0;i<count;i++){
                chartData.push({ title, count });
            }
    });

    return (
        <div className="clusters">
            <div className="displayPieChart">
                <div className="piechart-content">
                    <PieChart width={560} height={500}>
                        <Pie 
                            data={myData} 
                            dataKey="label_counts" 
                            nameKey="labels" 
                            outerRadius={200} 
                            innerRadius={130} 
                            labelLine={false}
                            cx={280}
                            cy={250}
                            animationDuration={0}
                            label={(entry) => entry.labels}
                            //onMouseEnter={handleInnerPieEnter}
                            onClick={handleInnerPieEnter}
                        >
                            {
                                myData.map((entry, index) => (
                                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]}>
                                    <Label 
                                        position="inside" 
                                        content={({ value }) => `${value} (${entry.labels})`}
                                    />
                                </Cell>
                                ))
                            }
                        </Pie>
                        <Legend />
                    </PieChart>
                </div>

                {selectedInnerData && (
                    <div className="piechart-content">
                        <h2>{selectedInnerData.labels}</h2>

                        <div>
                            {myData.map((post) => {
                                let { labels, documents} = post
                                
                                labels = labels[0]

                                if(selectedInnerData.labels[0] === labels){
                                    return documents.map((doc, i) => {
                                        let title = doc.title
                                        let content = doc.content
                                
                                        return (
                                            <div>
                                                <h4 key={i}>{title}</h4>
                                                <p key={i}>{content}</p>
                                            </div>
                                        )
                                    })
                                }
                            })}
                        </div> 
                    </div>
                )}
            </div>
        </div>  
    );
};

export default India;



