import React, { useState } from "react";

function Domain() {
  const [checked, setChecked] = useState([]);
  //const checkList = ["Social", "History", "Physics", "Finance", "Chemistry", "Geography", "Constitution", "Mathematics", "Machine learning", "Computer Science"];
  const checkList = ["Social", "History", "Physics", "Finance", "Chemistry"]

  // Add or Remove checked item from list
  const handleCheck = (event) => {
    var updatedList = [...checked];
    if (event.target.checked) {
      updatedList = [...checked, event.target.value];
    } else {
      updatedList.splice(checked.indexOf(event.target.value), 1);
    }
    setChecked(updatedList);
  };

  // Generate string of checked items
  const domaincheckedItems = checked.length 
    ? checked.reduce((total, item) => {
        return total + ", " + item;
      })
    : "";

  let isChecked = (item) =>
    checked.includes(item) ? "checked-item" : "not-checked-item";

  return (
    <div className="domain">
      <div className="domain-checkList">
        <div className="domain-title">Domain</div>
        <div className="domain-list-container">
          {
            checkList.map((item, index) => (
              <div key={index}>
                <input value={item} type="checkbox" onChange={handleCheck} />
                <span className={isChecked(item)}>{item}</span>
              </div>
            ))
          }
        </div>
      </div>

      <div>
        {`Items checked are: ${domaincheckedItems}`}
      </div>
    </div>
  );
}

export default Domain
