import axios from "axios"
import React, { useState, useEffect, useRef } from "react"
import ProgressBar from "./Progressbar/ProgressBar"
import { BiFullscreen } from "react-icons/bi"

function VideoDetails(props) {
  const [myData, setMyData] = useState([])
  const [searchKeyword, setSearchKeyword] = useState(0)
  const [currentTime, setCurrentTime] = useState(0)
  const [duration, setDuration] = useState(0)
  const [videotimeNow, setCurrentVideoTime] = useState(0) 
  const videoRef = useRef(null) 
  const chunks = []

  let localtitle = props.myTitle
  let videoFileName = props.myVideoFileName
  let showDiv = props.showDiv

  useEffect(() => {
    axios.get("./india.json").then((res) => setMyData(res.data))
  }, [])

  const removeDuplicateKeywords = () => {
    const list = []
    const set = new Set()

    return {
      push(element) {
        if (!set.has(element)) {
          list.push(element)
          set.add(element)
        }
      },
      get() {
        return list
      },
    }
  }

  let allKeywords = removeDuplicateKeywords()

  const handleSeekTo = (time) => {
    if(videoRef.current) {
      videoRef.current.currentTime = time
    }
  }

  const toSeconds = (timeString) => {
    timeString = timeString + ""
    let time = timeString.split(":")

    let hr = parseInt(time[0], 10)
    let min = parseInt(time[1], 10)
    let sec = parseInt(time[2], 10)

    time = hr * 3600 + min * 60 + sec

    return time
  }

  const [displayKeywordContainer,setDisplayKeywordContainer] = useState(false)

  const handleSearch = () => {
    setDisplayKeywordContainer(true)
    let selectedkeyword = document.getElementById("inputVal").value
    selectedkeyword = selectedkeyword.toUpperCase()

    if(selectedkeyword === "Default") 
      selectedkeyword = ""
    setSearchKeyword(selectedkeyword)
  }

  const handleTimeUpdate = (newTime) => {
    videoRef.current.currentTime = newTime
    setCurrentTime(newTime)
  }

  const handleDurationChange = () => {
    setDuration(videoRef.current.duration)
  }

  const progressTooltip = document.getElementById("progressTooltip");

  const [fullscreen, setFullscreen] = useState(false);

  const toggleFullScreen = async (event) => {
    const container = document.getElementById("video-chat-container");
    const fullscreenApi =
      container.requestFullscreen ||
      container.webkitRequestFullScreen ||
      container.mozRequestFullScreen ||
      container.msRequestFullscreen;

    if (!document.fullscreenElement) {
      setFullscreen(true);
      fullscreenApi.call(container);
    } else {
      document.exitFullscreen();
      setFullscreen(false);
    }
  };

  // Event handler to detect full-screen mode changes
  const handleFullScreenChange = () => {
    if (!document.fullscreenElement) {
      setFullscreen(false);
    }
  };

  // Register the event listener
  document.addEventListener("fullscreenchange", handleFullScreenChange);

  // Don't forget to remove the event listener when the component is unmounted
  useEffect(() => {
    return () => {
      document.removeEventListener("fullscreenchange", handleFullScreenChange);
    };
  }, []);

  function handleTime() {
    setCurrentTime(videoRef.current.currentTime)
    setCurrentVideoTime(videoRef.current.currentTime)
  }

  const displayTopic = (event) => {
    const rect = event.target.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const percentage = x / rect.width;
    const time = duration * percentage;
    let textToDisplay = '';

    for(let i=0;i<chunks.length;i++){
      let chunksData = chunks[i];

      if((time > chunksData.start) && (time < chunksData.end))
        textToDisplay = chunksData.topic
    }

    if(fullscreen === true){
      progressTooltip.style.display = "block";
      progressTooltip.style.left = `${event.clientX}px`;
      progressTooltip.style.top = `${rect.top - progressTooltip.offsetHeight}px`;
      progressTooltip.innerText = textToDisplay + " " + formatTime(time);
    }
  };
  
  const notdisplayTopic = () => {
    if(fullscreen === true){
      progressTooltip.style.display = "none";
    }
  };

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${String(seconds).padStart(2, "0")}`;
  }

  return (
    <div className="videoDetails">
      <div className="videoDetails-data">
        {myData.map((post) => {
          let { title, faculty, domain, keyframes_text, chapters_list, image_metadata, keyframes_timestamp, } = post

          title = title.toUpperCase()
          faculty = faculty.toUpperCase()
          domain = domain.toUpperCase()
          localtitle = localtitle.toUpperCase()

          if(title === localtitle) {
            let len = Object.keys(chapters_list).length
            let timeStamp = []
            let imgSrc

            for (let i = 1; i <= len; i++) {
              let obj = chapters_list[i]
              let startTime = obj[0]
              let keywords = obj[1]

              let imageData = image_metadata[i]
              let imagePath = imageData[0]

              if (keywords === searchKeyword) {
                imgSrc = imagePath
              }

              allKeywords.push(keywords)
              keywords = keywords.toUpperCase()

              let obj2 = keyframes_text[i]
              let endTime = obj2[1]
              let startTimeSeconds = toSeconds(startTime)
              let endTimeSeconds = toSeconds(endTime)

              const colors = [ "#FF0000", "#00FF00", "#0000FF", "#FFFF00", "#FF00FF", "#00FFFF", "#800000", "#008000", "#000080", "#808000", "#800080", "#008080", "#C0C0C0", "#800000", "#FFA500", "#FFFFF0", ]

              const newChunk = {
                start: startTimeSeconds,
                end: endTimeSeconds - 1.5,
                //color: colors[Math.floor(Math.random() * colors.length)],
                color: "gray",
                topic: keywords,
              }

              chunks.push(newChunk)

              if(searchKeyword === "") {
                return
              }
              if(keywords.includes(searchKeyword)) 
                timeStamp.push(startTime)
            }

            let startTime = toSeconds(timeStamp[0])

            return (
              <div>
                {showDiv && displayKeywordContainer &&(
                  <div className="keywordContainer">
                    <div className="photodiv-container">
                      <div className="keywordImage">
                        <img src={"rbi/"+imgSrc}></img>
                      </div>
                      <button id="seekToBtn" onClick = {handleSeekTo.bind(this, startTime)}>Seek to {timeStamp[0]}</button>
                    </div>
                  </div>
                )}
              </div>
            )
          }
        })}
      </div>

      <div className="videoDetails-video">
        <div class="row" id="video-chat-container">
          <video
            ref={videoRef}
            src={videoFileName}
            onTimeUpdate={handleTime}
            onDurationChange={handleDurationChange}
            height={359.6}
            controls
            onMouseMove={displayTopic}
            onMouseOut={notdisplayTopic}
            id="video-player"
          />

          <div id="top-overlay">
            <div>
              
                <button class="bi bi-fullscreen text-white text-outline" onClick={(event) => toggleFullScreen(event)}></button>
                <div id="progressTooltip"></div>
              
            </div>
          </div>

          <div id="bottom-overlay">
            <BiFullscreen
              id="fullscreen-toggle-btn"
              role="button"
              class="bi bi-fullscreen text-white text-outline"
              onClick={(event) => toggleFullScreen(event)}
            ></BiFullscreen>   
          </div>
        </div>

        <ProgressBar
          className="progressBar"
          id="normalProgressBar"
          currentTime={currentTime}
          duration={duration}
          onTimeUpdate={handleTimeUpdate}
          chunks={chunks}
          videotimeNow={videotimeNow}
          value={2}
        />
      </div>

      <div className="videoDetails-btn">
        <div className="keywords-container">
          <input id="inputVal" list="select-keyword" />

          <datalist id="select-keyword" placeholder="Pick a keyword">
            <option id="option-keyword" value="">
              Select a keyword
            </option>
            {allKeywords.get().sort().map((option, index) => {
              return <option key={index}> {option} </option>
            })}
          </datalist>

          <button className="videoDetails-searchBtn" id="videoDetails-searchBtn" onClick={handleSearch}>Search</button>
        </div>
      </div>
    </div>
  )
}

export default VideoDetails
