import axios from "axios";
import React, { useState, useEffect } from 'react'

function NewContent() {
    let [searchData, setSearchData] = useState('');
    let [teacher, setTeacher] = useState('');
    const [myData, setMyData] = useState([])
    const [isError, setIsError] = useState('')

    const onSearchChangeHandler = () => {
        let searchData = document.getElementById('searchData').value
        //console.log(searchData)
        setSearchData(searchData)
    }

    const onTeacherChangeHandler = () => {
        let selectedTeacher = document.getElementById('select-faculty').value
        console.log(selectedTeacher)
        setTeacher(selectedTeacher)
      }

    const getMyPostData = async () => {
        try {
            const res = await axios.get("/posts")
            setMyData(res.data)
        } catch (error) {
            setIsError(error.message)
        }
    }

    useEffect(() => {
        axios
        .get('./data.json')
        .then((res) => setMyData(res.data)
        )
    })

    return (
        <div className="container">
            <div className="searchFilter">  
                <div className='searchBar'>
                    <div className="search-input">
                        <input type="text" id="searchData" placeholder="Search"/>
                        <button type="submit" onClick={onSearchChangeHandler} id="search-btn" class="fa fa-search"></button>
                    </div>
                </div>
            </div>
            <div className="grid">
                {
                    myData.map((post) => {
                        let { domain, title, duration, faculty, video_url, transcript } = post
                    
                        searchData = searchData.toUpperCase()
                        title = title.toUpperCase()

                        facultyList = []

                        if(title.includes(searchData)){
                            facultyList.push(faculty)
                        }
                        
                        <select id='select-domain' onChange={onTeacherChangeHandler}>
                            <option></option>
                                {
                                facultyList.map((option, index) => {
                                    return <option key={index} > {option} </option>
                                })
                                }
                            </select>
                    })
                }
            </div>
        </div>
    )
}

export default NewContent
