import axios from "axios";
import { useState, useEffect } from "react";
import { PieChart, Pie } from "recharts";

const IndiaCluster = () => {
    const [myData, setMyData] = useState([])

    useEffect(() => {
        axios.get('./india-clusters.json')
          .then((res) => setMyData(res.data)
        )
    }, [0]);

    return (  
        <div className="clusters">
            <div>
                {
                    myData.map((post) => {
                        let { labels,documents} = post
                        
                        labels = labels[0]

                        for(let i=0;i<documents.length;i++){
                            let title = documents[i].title
                            let content = documents[i].content
                        }
                    })
                }
            </div> 

            <div>
                <PieChart width={500} height={500}>
                    <Pie 
                        data={myData} 
                        dataKey="label_counts" 
                        
                        outerRadius={200} 
                        fill="green" 
                        
                        animate
                        animationDuration={40}
                        animationEasing="ease-in"
                        center={[50, 50]}
                        lineWidth={15}
                        lengthAngle={360}
                        paddingAngle={0}
                        radius={50}
                        rounded
                        startAngle={0}
                        viewBoxSize={[100, 100]}
                        labelStyle={{
                            fontSize: "6px",
                            fontColor: "FFFFFA",
                            fontWeight: "500",
                            fontFamily: "monospace"
                        }}
                        label={(myData) => myData.dataEntry.labels}
                        labelPosition={70}
                    />
                </PieChart>
            </div>
        </div>
    )
}

export default IndiaCluster
