import React from "react";
import { useLocation } from "react-router-dom";
import ReactPlayer from 'react-player';

function VideoDetails() {
    let location = useLocation();
    let video_url = location.state;

    video_url = video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)

    const [isPlaying, setIsPlaying] = React.useState(true);
    const [isReady, setIsReady] = React.useState(false);
    const p = React.useRef();
    
    const onReady = React.useCallback(() => {
        if(!isReady) {
            const timeToStart = 1*60+50;
            playerRef.current.seekTo(timeToStart, "seconds");
            setIsReady(true);
        }
    }, [isReady]);

    return (
        <div className="videoDetails">
            <div className="videoDetails-video">
                <ReactPlayer
                    ref={p}
                    url={video_url}
                    playing={isPlaying}
                    width="100%"
                    height="100%"
                    controls={true}
                    autoPlay={true}
                    onReady={onReady}
                />

                <button onClick={() => this.p.seekTo(12.7)}>Seek to 12.7</button>
                <button onClick={() => this.p.seekTo(22.65)}>Seek to 22.65</button>
            </div>
        </div>
    )
}

export default VideoDetails











