import axios from "axios"
import { Link } from "react-router-dom"
import React, { useState, useEffect } from "react"
import { PieChart, Pie, Label, Tooltip, Cell, Legend } from "recharts"

const Charts = (props) => {
  const searchData = props.searchData
  const [myData, setMyData] = useState([])
  const [selectedInnerData, setSelectedInnerData] = useState(null)
  const InnerColors = ["#0088FE","#00C49F","#FFBB28","#FF8042","#AF19FF","#F2F2F2",]

  const [selectedOuterData, setSelectedOuterData] = useState(null)
  const OuterColors = [ "#FF6384", "#36A2EB", "#FFCE56", "#1abc9c", "#3498db", "#9b59b6", "#34495e", "#f1c40f", "#e74c3c", "#95a5a6", "#2ecc71", "#8e44ad", "#d35400", "#7f8c8d", "#27ae60", ]
  const [OuterChartData, setOuterChartData] = useState([])

  let [showInnerContent, setShowInnerContent] = useState(false)
  let [showAllInnerContent, setShowAllInnerContent] = useState(true)
  let [showOuterContent, setShowOuterContent] = useState(false)
  let flag = true

  const handleInnerPieEnter = (data) => {
    setSelectedInnerData(data)
    setShowOuterContent(false)
    setShowAllInnerContent(false)
    setShowInnerContent(true)
  }

  const handleOuterPieEnter = (data) => {
    setSelectedOuterData(data)
    setShowInnerContent(false)
    setShowAllInnerContent(false)
    setShowOuterContent(true)
  }

  let jsonPath, innerlabelCount=0, outerlabelCount=0, allLabelCount=0

  if ("INDIA".includes(searchData)) {
    jsonPath = "./india-cluster.json"
  } else if ("PHYSICS".includes(searchData)) {
    jsonPath = "./physics-cluster.json"
  } else if ("LAW".includes(searchData)) {
    jsonPath = "./law-cluster.json"
  }

  useEffect(() => {
    axios.get(jsonPath).then((res) => setMyData(res.data))
  }, [searchData])

  useEffect(() => {
    let newOuterChartData = []
    myData.forEach((post) => {
      let { labels,documents } = post
      
      for(let i=0; i<documents.length; i++) {
        let title = documents[i].title
        let titleCount = 1
        newOuterChartData.push({ title: title, count: titleCount })
      }
    })
    setOuterChartData(newOuterChartData)
  }, [myData])

  const linkStyle = {
    color: 'black',
    fontSize: '18px'
  };

  const handleClick = (title,videoFileName) => {
    localStorage.setItem('title', title);
    localStorage.setItem('videoFileName', videoFileName);
  }

  const displayContent = (title,content,transcript,videoFileName) => {
    return (
      <div className="displayChartContent">
        <h5 id="chart-title">{title}</h5>
        <p>{content}</p>
        <button id='view-VideoDetails' onClick={handleClick.bind(this,title,videoFileName)}>
          <Link to="/videoDetails" style={linkStyle} target="_blank">Watch Video</Link>
        </button>
      </div>
    )
  }

  const [selectedInnerSliceIndex, setSelectedInnerSliceIndex] = useState(null);
  const [selectedOuterSliceIndex, setSelectedOuterSliceIndex] = useState(null);
  
  const handleInnerSliceClick = (index) => {
    setSelectedOuterSliceIndex(null);
    setSelectedInnerSliceIndex(index);
  };
  
  const handleOuterSliceClick = (index) => {
    setSelectedInnerSliceIndex(null);
    setSelectedOuterSliceIndex(index);
  };

  return (
    <div className="displayPieChart">
      {myData.map((post) => {
        let { labels, documents } = post

        labels = labels[0]
        
        if(selectedInnerData)
          innerlabelCount = selectedInnerData.documents.length

        for(let i=0;i<documents.length;i++){
          allLabelCount++;
        }
      })}
      
      <div className="piechart-content" id="pieChartView">
        <PieChart width={650} height={500}>
          <Pie
            data={OuterChartData} //Outer PieChart
            dataKey="count"
            nameKey="title"
            cx={319}
            cy={200}
            labelLine={false}
            //isAnimationActive={false}
            animationDuration={0}
            outerRadius={140}
            innerRadius={110}
            label={(entry) => entry.title}
            onClick={handleOuterPieEnter}
          >
            {OuterChartData.map((entry, index) => (
              <Cell
                key={`cell-${index}`}
                fill={OuterColors[index % OuterColors.length]}
                stroke={selectedOuterSliceIndex === index ? 'black' : 'none'}
                strokeWidth={selectedOuterSliceIndex === index ? 2 : 0}
                onClick={() => handleOuterSliceClick(index)}
              />
            ))}
          </Pie>

          <Pie
            data={myData} //Inner PieChart
            dataKey="label_counts"
            nameKey="labels"
            outerRadius={45}
            innerRadius={20}
            labelLine={false}
            //isAnimationActive={false}
            animationDuration={0}
            cx={319}
            cy={200}
            label={(entry) => entry.labels}
            onClick={handleInnerPieEnter}
          >
            {myData.map((entry, index) => (
              <Cell
              key={`cell-${index}`}
              fill={InnerColors[index % InnerColors.length]}
              stroke={selectedInnerSliceIndex === index ? 'black' : 'none'}
              strokeWidth={selectedInnerSliceIndex === index ? 2 : 0}
              onClick={() => handleInnerSliceClick(index)}
            />
            ))}
          </Pie>
        </PieChart>
      </div>

      <div className="piechart-content" id="piechart-content">
        {showInnerContent && selectedInnerData && (
          <div className="piechart-content">
            <div className="label-count"><h5>All retrieved results: {innerlabelCount}</h5> </div>
            <div>
              {myData.map((post) => {
                let { labels, documents } = post

                labels = labels[0]

                if (selectedInnerData.labels[0] === labels) {
                  return documents.map((doc, i) => {
                    let title = doc.title
                    let content = doc.content
                    let transcript = doc.transcript
                    let videoFileName = './rbi/' + doc.video_File_Name + '.mp4'

                    return (<div key={i}> {displayContent(title,content,transcript,videoFileName)} </div>)
                  })
                }
              })}
            </div>
          </div>
        )}

        {showOuterContent && (
          <div className="piechart-content">
            <div className="label-count"><h5>All retrieved results: {1}</h5> </div>
            {myData.map((post) => {
              let { documents } = post

              return documents.map((doc, i) => {
                let title = doc.title
                let content = doc.content
                let transcript = doc.transcript
                let videoFileName = './rbi/' + doc.video_File_Name + '.mp4'

                if (title === selectedOuterData?.title && flag === true) {
                  flag = false

                  return (<div key={i}> {displayContent(title,content,transcript,videoFileName)} </div>)
                }
              })
            })}
          </div>
        )}

        {showAllInnerContent && (
          <div className="piechart-content">
            <div className="label-count"><h5>All retrieved results: {allLabelCount}</h5> </div>
            <div>
              {myData.map((post) => {
                let { labels, documents } = post

                labels = labels[0]

                return documents.map((doc, i) => {
                  let title = doc.title
                  let content = doc.content
                  let transcript = doc.transcript
                  let videoFileName = './rbi/' + doc.video_File_Name + '.mp4'

                  return (<div key={i}> {displayContent(title,content,transcript,videoFileName)} </div>)
                })
              })}
            </div>
          </div>
        )}
      </div>
    </div>
  )
}

export default Charts
