import axios from "axios";
import React, { useState, useEffect } from "react";
import { PieChart, Pie, Label, Tooltip, Cell, Legend } from "recharts";

const Charts = (props) => {
    const searchData = props.searchData;
    const [myData, setMyData] = useState([])
    const [selectedInnerData, setSelectedInnerData] = useState(null)
    const InnerColors = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#AF19FF', '#F2F2F2'];
    
    const [selectedOuterData, setSelectedOuterData] = useState(null)
    const OuterColors = ["#FF6384","#36A2EB","#FFCE56","#1abc9c","#3498db","#9b59b6","#34495e","#f1c40f","#e74c3c","#95a5a6","#2ecc71","#8e44ad","#d35400","#7f8c8d","#27ae60"];
    const [OuterChartData, setOuterChartData] = useState([])

    const [showInnerContent, setShowInnerContent] = useState(false);
    const [showOuterContent, setShowOuterContent] = useState(false);
    let flag = true

    const handleInnerPieEnter = (data) => {
        setSelectedInnerData(data);
        setShowOuterContent(false);
        setShowInnerContent(true);
    };

    const handleOuterPieEnter = (data) => {
        setSelectedOuterData(data);
        setShowInnerContent(false);
        setShowOuterContent(true);
    };

    let jsonPath;

    if("INDIA".includes(searchData)) {
        
        jsonPath = './india-cluster.json';
    }
    else if("PHYSICS".includes(searchData)) {
        
        jsonPath = './physics-cluster.json';
    }
    else if("LAW".includes(searchData)) {
        
        jsonPath = './law-cluster.json';
    }

    useEffect(() => {
        axios.get(jsonPath)
        .then((res) => setMyData(res.data));
    }, [searchData]);

    useEffect(() => {
        let newOuterChartData = []
        myData.forEach((post) => {
          let { documents } = post
          for(let i=0;i<documents.length;i++){
            let title = documents[i].title
            let titleCount = 1
            newOuterChartData.push({ title: title, count: titleCount })
          }
        })
        setOuterChartData(newOuterChartData)
    }, [myData]);

    return (  
        <div className="clusters">
            <div className="displayPieChart">
                <div className="piechart-content">
                    <PieChart width={560} height={500}>
                        <Pie
                            data={OuterChartData}       //Outer PieChart
                            dataKey="count"
                            nameKey="title"
                            cx={270} 
                            cy={240} 
                            labelLine={false}
                            animationDuration={0}
                            outerRadius={140}
                            innerRadius={110} 
                            label={(entry) => entry.title}
                            onClick={handleOuterPieEnter}
                        >
                            {
                                OuterChartData.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={OuterColors[index % OuterColors.length]}>
                                        <Label
                                            value={entry.title}
                                            position="inside"
                                            fill="#fff"
                                            fontSize={14}
                                        />
                                    </Cell>
                                ))
                            }
                        </Pie>
                   
                        <Pie 
                            data={myData}       //Inner PieChart
                            dataKey="label_counts" 
                            nameKey="labels"
                            outerRadius={50} 
                            innerRadius={20} 
                            labelLine={false}
                            animationDuration={0}
                            cx={270}
                            cy={240}
                            label={(entry) => entry.labels}
                            onClick={handleInnerPieEnter}
                        >
                            {
                                myData.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={InnerColors[index % InnerColors.length]}>
                                        
                                    </Cell>
                                ))
                            }
                        </Pie>
                        
                        
                    </PieChart>
                </div>

                <div className="piechart-content">

                    {showInnerContent && selectedInnerData && (
                        <div className="piechart-content">
                            <h2>{selectedInnerData.labels}</h2>
    
                            <div>
                                {myData.map((post) => {
                                    let { labels, documents} = post
                                    
                                    labels = labels[0]
    
                                    if(selectedInnerData.labels[0] === labels){
                                        return documents.map((doc, i) => {
                                            let title = doc.title
                                            let content = doc.content
                                    
                                            return (
                                                <div>
                                                    <h4 key={i}>{title}</h4>
                                                    <p key={i}>{content}</p>
                                                </div>
                                            )
                                        })
                                    }
                                })}
                            </div> 
                        </div>
                    )}

                    {showOuterContent && (
                        <div className="piechart-content">   
                        <h2>{selectedOuterData && selectedOuterData.title}</h2> 
                        {
                            myData.map((post) => {
                                let { documents } = post
                                //let flag = true
                                
                                for(let i=0;i<documents.length;i++){
                                    let title = documents[i].title
                                    if(title === selectedOuterData?.title  && flag===true) {
                                        flag = false
                                        
                                        return (
                                            <div>
                                                <p key={i}>{documents[i].content}</p>
                                            </div>
                                        )
                                    }
                                }
                            })
                        }
                    </div>
                    )}
                </div>
            </div>
        </div>
    )
}

export default Charts
