import axios from "axios";
import React, { useState, useEffect} from "react";
import ReactPlayer from "react-player";

function VideoDetails() {
    let localtitle = localStorage.getItem('title')
    let video_url = localStorage.getItem('videoUrl')
    video_url = video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)

    //var parent = document.getElementById("parentDiv");

    var input = document.createElement("input");
    input.type = "text";
    input.className = "videDetails-search";
    document.body.appendChild(input);

    var searchBtn = document.createElement("button");
    var text = document.createTextNode("Search");
    searchBtn.className = "videDetails-searchBtn"
    searchBtn.appendChild(text)
    document.body.appendChild(searchBtn);

    fetch('india.json')
        .then(response => response.json())
        .then(data => {
            for(let i in data){
                let title = data[i].title
                let keyframes_text = data[i].keyframes_text

                if(title === localtitle){
                    let len = Object.keys(keyframes_text).length
                    let timeStamp = [];
                    let allKeywords = []

                    for(let i=1;i<=len;i++){
                        let obj = keyframes_text[i]
                        let startTime = obj[0]
                        let endTime = obj[1]
                        let keywords= obj[2].slice(1,obj[2].length-1).split(',')

                        keywords.forEach(function(key,j) {
                            allKeywords.push(key)
                            key = key.toUpperCase()

                            let searchKeyword = document.getElementById('videDetails-search').value

                            if(searchKeyword === '')
                                return
                            if(key.includes(searchKeyword))
                                timeStamp.push(startTime)
                        })
                    }

                    
                }
            }
        })
        .catch(error => {
            console.error('Error fetching JSON data:', error);
        });
}

export default VideoDetails
