import axios from "axios";
import React, { useState, useEffect } from "react";
import { PieChart, Pie, Cell, Legend } from 'recharts';

const India = () => {
    const [data, setMyData] = useState([])
    const COLORS = ["#FF6384","#36A2EB","#FFCE56","#1abc9c","#3498db","#9b59b6","#34495e","#f1c40f","#e74c3c","#95a5a6","#2ecc71","#8e44ad","#d35400","#7f8c8d","#27ae60"];

    useEffect(() => {
      axios.get('./india-clusters.json')
        .then((res) => setMyData(res.data)
      )
    }, [0]);

    const titles = data.reduce((acc, { documents }) => {
        documents.forEach(({ title }) => {
            acc[title] = (acc[title] || 0) + 1;
        });
        return acc;
    }, {});

    const chartData = Object.entries(titles).map(([title, count]) => ({ title, count }));
    console.log(chartData)

    return (
        <PieChart width={400} height={400}>
            <Legend />
            <Pie
                data={chartData}
                dataKey="count"
                nameKey="title"
                cx="50%"
                cy="50%"
                outerRadius={80}
                fill="#8884d8"
                label
            >
                {
                    chartData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))
                }
            </Pie>
        </PieChart>
    );
};

export default India;



