import React, { useState, useEffect } from 'react'
import SearchBar from './SubComponents/SearchBar'
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Popover from 'react-bootstrap/Popover';
import axios from "axios"

function Content() {
  let [searchData, setSearchData] = useState('');
  let [teacher, setTeacher] = useState('');
  let [domains, setDomains] = useState('');

  const teacherList = ["Kohli", "David", "Sharma", "Singh", "Chopra", "Kalaam"];
  const domainsList = ["Social", "Finance", "History", "Physics", "Chemistry", "Mathematics" , "Geography", "Constitution", "Machine Learning", "Computer Science"];

  const getSearchData = (data) => {
    setSearchData(data);
  };

  const onTeacherChangeHandler = (event) => {
    setTeacher(event.target.value)
  }

  const onDomainsChangeHandler = (event) => {
    setDomains(event.target.value)
  }

  const [myData, setMyData] = useState([])
  const [isError, setIsError] = useState('')

  const getMyPostData = async () => {
    try {
      const res = await axios.get("/posts")
      setMyData(res.data)
    } catch (error) {
      setIsError(error.message)
    }
  }

  useEffect(() => {
    axios.get('./data.json')
      .then((res) => setMyData(res.data)
    )
  })

  const downloadTxtFile = (transcript) => {
    const element = document.createElement('a');
    
    const file = new Blob([transcript], {
      type: "text/plain; charset=utf-8",
    });

    element.href = URL.createObjectURL(file);
    element.download = "transcript.txt";
    document.body.appendChild(element);
    element.click();
  }

  const viewTranscript = (transcript) => {
    <OverlayTrigger
      trigger="click"
      key="left"
      rootClose
      placement="left"
      overlay={
        <Popover id={`popover-positioned-left`}>
          <Popover.Header as="h3">Transcript</Popover.Header>
          <Popover.Body style={{height:"400px",width:"280px", overflow:"hidden"}}>
            {/* <strong>Holy guacamole!</strong> Check this info. */}
            <div style={{overflowY:"scroll",height:"400px"}}>{transcript}</div>
          </Popover.Body>
        </Popover>
      }
    >
    </OverlayTrigger>
  }

  const closeTranscript = () => {
    document.querySelector('.displayTranscript').style.display = 'none'
  }

  const displayVideoContent = function(video_url,title,duration,faculty,newTranscript,transcript) {
    return (
      <div className='videos-content'>
        <div className="videoContent">
          <video width="500" height="290" controls>
            <source type="video/mp4" src={video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)}></source>
          </video>
        </div>
        <div className="videoContent">
          <p>Title: {title}</p>
          <p>Duration: {duration}</p>
          <p>Faculty: {faculty}</p>
          <p>{newTranscript}</p>

          <button id='download-btn' onClick={downloadTxtFile.bind(this,transcript)}>Download transcript</button>
          
          <OverlayTrigger
          trigger="click"
          key="left"
          rootClose
          placement="left"
          overlay={
            <Popover id={`popover-positioned-left`}>
              <Popover.Header as="h3">Transcript</Popover.Header>
              <Popover.Body style={{height:"400px",width:"280px", overflow:"hidden"}}>
                {/* <strong>Holy guacamole!</strong> Check this info. */}
                <div style={{overflowY:"scroll",height:"400px"}}>{video.transcript}</div>
              </Popover.Body>
            </Popover>
          }
        >
          <Button  style={{marginLeft:"250px"}} variant="secondary">Click here to view transcript</Button>
        </OverlayTrigger>

          <div className='displayTranscript'></div>
        </div>  
      </div>
    )
  }

  return (
    <div className="container">
      <div className="searchFilter">  
        <div className='searchBar'>
          <SearchBar onSubmit={getSearchData}/>   
        </div>     
        
        <div className='facultyDomain'>
          <div className="faculty-container">
            <div className='faculty-title'>Faculty</div>
            <select onChange={onTeacherChangeHandler}>
              <option>Select faculty</option>
                {
                  teacherList.map((option, index) => {
                    return <option key={index} > {option} </option>
                  })
                }
            </select>
          </div>

          <div className="domain-container">
          <div className='domain-title'>Domain</div>
            <select onChange={onDomainsChangeHandler}>
              <option>Select domain</option>
                {
                  domainsList.map((option, index) => {
                    return <option key={index} > {option} </option>
                  })
                }
            </select>
          </div>
        </div>
      </div>

      <div className="grid">
        {
          myData.map((post) => {
            if(searchData == ''){
              return
            }
            else{
              //document.querySelector('.facultyDomain').style.display = 'block'

              let { domain, title, duration, faculty, video_url, transcript } = post
              
              searchData = searchData.toUpperCase()
              domain = domain.toUpperCase()
              domains = domains.toUpperCase()
              teacher = teacher.toUpperCase()
              faculty = faculty.toUpperCase() 
              title = title.toUpperCase() 

              let i=0, count=0
              const maxSentences = 5
              let newTranscript=''

              while(i < transcript.length){
                if(count == maxSentences)
                  break
                if(transcript[i] == '.')
                  count++;
                newTranscript = newTranscript + transcript[i++];
              }

              if(title.includes(searchData) || domain.includes(searchData) || faculty.includes(searchData)){
                if((teacher === faculty) && (domain === domains))
                  return (<div> {displayVideoContent(video_url,title,duration,faculty,newTranscript,transcript)} </div>)
                
                else if((teacher === faculty) && (domains == ''))
                  return (<div> {displayVideoContent(video_url,title,duration,faculty,newTranscript,transcript)} </div>)
                
                else if((teacher == '') && ((domain === domains)))
                  return (<div> {displayVideoContent(video_url,title,duration,faculty,newTranscript,transcript)} </div>)

                else if((teacher === '') && (domain === domains))
                  return (<div> {displayVideoContent(video_url,title,duration,faculty,newTranscript,transcript)} </div>)
                
                else if(teacher == '' && domains == '')
                  return (<div> {displayVideoContent(video_url,title,duration,faculty,newTranscript,transcript)} </div>)
              }
            }
          })
        }
      </div>
    </div>
  )
}

export default Content
