import axios from "axios"
import React, { useState, useEffect } from "react"
import SearchBar from "./SubComponents/SearchBar"
import List from "./SubComponents/PieChart/List"
import Charts from "./SubComponents/PieChart/Charts"

const Content = () => {
  const [myData, setMyData] = useState([])
  let [searchData, setSearchData] = useState("") //Search Value

  // Setting the unique id for the key, so that it refresh the displayed data when a page is refreshed
  let uniqueId = Math.floor(Math.random() * 100) + 1;

  // List & PieChart view
  let showChartList = false
  let [showPieChartContent, setShowPieChartContent] = useState(false)
  let [showListContent, setShowListContent] = useState(false)

  // Get search data from SearchBar.js and setting the data
  const getSearchData = (data) => {
    data = data.toLowerCase()
    setSearchData(data)
    setShowListContent(true)      //Default view need to be list view
  }

  // Getting the data from JSON file
  useEffect(() => {
    axios.get("data.json").then((res) => {
      setMyData(res.data)
    })
  }, [])
  
  const [isClicked, setIsClicked] = useState(false);    // By default list button need to be highlited

  // Display list content only and hide piechart content
  const handleListContent = () => {
    setIsClicked(true)
    var listBtn = document.getElementById("listBtn");
    var pieChartBtn = document.getElementById("pieChartBtn");

    pieChartBtn.style.backgroundColor = "";     // Remove highlight from pieChartBtn
    listBtn.style.backgroundColor = "grey";

    showPieChartContent = false
    setShowListContent(true)
  }

  // Display piechart content only and hide list content
  const handlePieChartContent = () => {
    var pieChartBtn = document.getElementById("pieChartBtn");
    var listBtn = document.getElementById("listBtn");

    listBtn.style.backgroundColor = "";        // Remove highlight from listBtn
    pieChartBtn.style.backgroundColor = "grey";

    setShowListContent(false)
    showPieChartContent = true
  }

  // Calling the List from list.js 
  const displayListData = (searchData) => {
    return (
      <div className="list">
        <List searchData={searchData} />
      </div>
    )
  }

  // Calling the piechart from piechart.js 
  const displayPieChartData = (searchData) => {
    return (
      <div className="PieChart">
        <Charts searchData={searchData} />
      </div>
    )
  }

  // Adding style when the data is searched for the first time
  if(searchData) {
    document.querySelector('.searchFilter').style.paddingTop = "0.7rem";
    document.querySelector('.header').style.display = 'block'
  }

  return (
    <div className="container">
      <div className="header">        {/* Display the company logo and Homepage */}
        <li className="navbar">
          <a id="header-content"><img id="header-logo" src='/logo/img3.png'></img></a>
          <a id="home" href="./">Home</a>
        </li>
      </div>

      {!searchData && (
        <div className="homepage-content">
          <img id='homepage-logo' src="/logo/img2.png"></img>
        </div>
      )}

      {/* Calling the search bar from the SearchBar.js files */}
      <div className="searchFilter">
        <div className="searchBar">
          <SearchBar onSubmit={getSearchData} />
        </div>
      </div>

      <div className="grid">
        {myData.map((item) => {
          if(searchData !== "") {
            showChartList = true
            
            //Getting data from JSON file which is stored myData, & accessing this by using map() -> {currentItems.map()}
            let { domain, title, duration, faculty, video_url, transcript } = item

            searchData = searchData.toUpperCase()
            domain = domain.toUpperCase()
            faculty = faculty.toUpperCase()
            title = title.toUpperCase()
          }
        })}
      </div>

      {/* Displaying the list and piechart */}
      {showChartList && (
        <div className="chartList">
          <button id="listBtn" style={{ backgroundColor: isClicked ? 'default' : 'grey' }} onClick={handleListContent}>List</button>
          <button id="pieChartBtn" onClick={handlePieChartContent}>Pie Chart</button>

          {showPieChartContent && <div key={uniqueId}>{displayPieChartData(searchData)}</div>}
          {showListContent && <div key={uniqueId+1}>{displayListData(searchData)}</div>}
        </div>
      )}
    </div>
  )
}

export default Content
