import axios from "axios";
import { useState, useEffect } from "react";
import { PieChart, Pie, Label } from "recharts";

const IndiaCluster = () => {
    const [myData, setMyData] = useState([])

    useEffect(() => {
        axios.get('./india-clusters.json')
          .then((res) => setMyData(res.data)
        )
    }, [0]);

    return (  
        <div className="clusters">
            <div>
                {
                    myData.map((post) => {
                        let { labels,documents} = post
                        
                        labels = labels[0]

                        for(let i=0;i<documents.length;i++){
                            let title = documents[i].title
                            let content = documents[i].content
                        }
                    })
                }
            </div> 

            <div>
                <PieChart width={500} height={500}>
                    <Pie data={myData} dataKey="label_counts" nameKey="labels" outerRadius={200} innerRadius={130}>
                        {myData.map((entry, index) => (
                            <Label 
                                key={`label-${index}`} 
                                position="center"
                                fill="Black" 
                                fontSize={30}
                            > {entry.labels}
                            </Label>
                        ))}
                    </Pie>
                </PieChart>
            </div>
            <div>
                <PieChart
                    animate
                    animationDuration={40}
                    animationEasing="ease-in"
                    center={[50, 50]}
                    data={myData}
                    lineWidth={15}
                    lengthAngle={360}
                    paddingAngle={0}
                    radius={50}
                    rounded
                    startAngle={0}
                    viewBoxSize={[100, 100]}
                    labelStyle={{
                        fontSize: "20px",
                        fontColor: "FFFFFA",
                        fontWeight: "500",
                        fontFamily: "monospace"
                    }}
                    label={myData.map((entry, index) => (
                        <Label> {entry.labels}</Label>))}
                    labelPosition={70}
                />
            </div>
        </div>
    )
}

export default IndiaCluster

