import { useState, useEffect } from "react"
import axios from "axios"

const VideoContent = (props) => {
  const [myData, setMyData] = useState([])
  const [isError, setIsError] = useState("")

  let searchData = props.searchData;
  let domains = props.domains;
  let teacher  = props.teacher;

  const getMyPostData = async () => {
    try {
      const res = await axios.get("/posts")
      setMyData(res.data)
    } catch (error) {
      setIsError(error.message)
    }
  }

  useEffect(() => {
    axios.get('./data.json')
      .then((res) => setMyData(res.data)
    )
  })

  function display(title, duration, faculty){
    return (
        <div className="videoContent">
          <p>Title: {title}</p>
          <p>Duration: {duration}</p>
          <p>Faculty: {faculty}</p>

        </div>
    )
  }

  return (
    <>
      <div className="video-grid">
        {
          myData.map((post) => {
            let {title, duration, domain, faculty, transcript} = post;

            if((title.includes(searchData)) || (faculty.includes(searchData)) || (domain.includes(searchData))){  
              if((teacher === faculty) && (domain === domains))
                return (<div> {display(title, duration, faculty, newTranscript)} </div>)
              
              else if((teacher === faculty) && (domains == ''))
                return (<div> {display(title, duration, faculty, newTranscript)} </div>)
              
              else if((teacher === '') && (domain === domains))
                return (<div> {display(title, duration, faculty, newTranscript)} </div>)
              
              else if(teacher == '' && domains == '')
                return (<div> {display(title, duration, faculty, newTranscript)} </div>)
            }  
          })
        }   
      </div>
    </>    
  )
}

export default VideoContent
