import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Popover from 'react-bootstrap/Popover';

const downloadTxtFile = (title,transcript) => {
  const element = document.createElement('a');
  element.style.fontFamily = 'Arial';
  element.style.fontSize = '20px';
  element.style.textAlign = 'justify';
  
  const file = new Blob([transcript], {
    type: "text/html; charset=utf-8",
  });

  element.href = URL.createObjectURL(file);
  element.download = title + ".doc";
  document.body.appendChild(element);
  element.click();
} 

const closeTranscript = () => {
  document.querySelector('#displayTranscript').style.display = 'none'
}

const VideoContent = (props) => {
  const title = props.title;
  const duration = props.duration;
  const faculty = props.faculty;
  const transcript = props.transcript;
  const newTranscript = props.newTranscript; 

  return (
    <div className="videoContent">
      <div className="videoUpperContent">
        <p>Title: {title}</p>
        <p>Duration: {duration}</p>
        <p>Faculty: {faculty}</p>
        <p>{newTranscript}</p>
      </div>

      <div className="videoLowerContent">
        <button id='download-btn' onClick={downloadTxtFile.bind(this,title,transcript)}>Download transcript</button>
        
        <OverlayTrigger
          trigger="click" rootClose placement="top"
          overlay={
            <Popover id={`displayTranscript`} style={{height:"260px",width:"400px"}}>
              <Popover.Header as="h3">
                Transcript
                <button id='closeTranscript' onClick={closeTranscript.bind(this)}><i class="fa fa-close"></i></button>
              </Popover.Header>
              <Popover.Body style={{height:"205px",width:"290px", overflow:"hidden"}}>
                <div style={{overflowY:"scroll",height:"200px"}}>{transcript}</div>
              </Popover.Body>
            </Popover>
          }
        > 
        <button id='viewTranscipt-btn'>Click here to view transcript</button>
        </OverlayTrigger>
      </div>
    </div>  
  )
}

export default VideoContent
