import React from "react";
import { useLocation } from "react-router-dom";
import ReactPlayer from 'react-player';

function VideoDetails() {
    let location = useLocation();
    let video_url = location.state;

    video_url = video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)

    const [isPlaying, setIsPlaying] = React.useState(true);
    const [isReady, setIsReady] = React.useState(false);
    const playerRef = React.useRef();

    const timeStamp = ["0:10", "0:50", "1:00" , "1:10"];
    
    for(let i in timeStamp){
        let time = timeStamp[i].split(":");
        
        time = time[0]*60 + time[1]
    }

    const onReady = React.useCallback(() => {
        if(!isReady) {
            const timeToStart = (6 * 60) + 55;
            playerRef.current.seekTo(timeToStart, "seconds");
            setIsReady(true);
        }
    }, [isReady]);


    return (
        <div className="videoDetails">
            <div className="videoDetails-video">
                <ReactPlayer
                    ref={playerRef}
                    url={video_url}
                    playing={isPlaying}
                    width="100%"
                    height="100%"
                    controls={true}
                    autoplay={true}
                    onReady={onReady}
                />
            </div>
        </div>
    )
}

export default VideoDetails

