        }
    }, [isReady]);


    return (
        <div className="videoDetails">
            <div className="videoDetails-video">
                <ReactPlayer
                    ref={playerRef}
                    url={video_url}
                    playing={isPlaying}
                    width="100%"
                    height="100%"
                    controls={true}
                    autoPlay={true}
                    onReady={onReady}
                />
            </div>
        </div>
    )
}

export default VideoDetails

