import axios from "axios";
import React, { useState, useEffect } from "react";
import { PieChart, Pie, Label, Tooltip, Cell } from "recharts";

const IndiaCluster = () => {
    const [myData, setMyData] = useState([])
    const [selectedData, setSelectedData] = useState(null)
    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#AF19FF', '#F2F2F2'];
    
    const handlePieEnter = (data) => {
        setSelectedData(data);
    };

    useEffect(() => {
        axios.get('./india-clusters.json')
          .then((res) => setMyData(res.data)
        )
    }, [0]);

    return (  
        <div className="clusters">
            <div className="displayPieChart">
                <div className="piechart-content">
                    <PieChart width={560} height={500}>
                        <Pie 
                            data={myData} 
                            dataKey="label_counts" 
                            nameKey="labels" 
                            outerRadius={200} 
                            innerRadius={130} 
                            labelLine={false}
                            cx={280}
                            cy={250}
                            animationDuration={0}
                            label={(entry) => entry.labels}
                            //onMouseEnter={handlePieEnter}
                            onClick={handlePieEnter}
                            isAnimationActive={true}
                        >
                            {
                                myData.map((entry, index) => (
                                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]}>
                                    <Label 
                                        position="inside" 
                                        content={({ value }) => `${value} (${entry.labels})`}
                                    />
                                </Cell>
                                ))
                            }
                        </Pie>
                    </PieChart>
                </div>

                {selectedData && (
                    <div className="piechart-content">
                        <h2>{selectedData.labels}</h2>

                        <div>
                            {myData.map((post) => {
                                let { labels,documents} = post
                                
                                labels = labels[0]

                                if(selectedData.labels[0] === labels){
                                    return documents.map((doc, i) => {
                                        let title = doc.title
                                        let content = doc.content
                                
                                        return (
                                            <div>
                                                <h4 key={i}>{title}</h4>
                                                <p key={i}>{content}</p>
                                            </div>
                                        )
                                    })
                                }
                            })}
                        </div> 
                    </div>
                )}
            </div>
        </div>
    )
}

export default IndiaCluster



