import axios from "axios";
import React, { useState, useEffect} from "react";
import ReactPlayer from "react-player";

function VideoDetails() {
    const [instance, setInstance] = useState(null);
    const [searchKeyword, setSearchKeyword] = useState("");

    let localtitle = localStorage.getItem('title')
    let video_url = localStorage.getItem('videoUrl')
    video_url = video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)

    const handleStart = (startTime) => {
        if(instance) {
          if(isNaN(startTime)) {
            startTime = "0.0";
          }
          instance.seekTo(startTime);
        }
    };

    const handleClick = () => {
        let searchData = document.getElementById('searchKeyword').value;
        searchData = searchData.toUpperCase()
        
        setSearchKeyword(searchData);
      }

    fetch('india.json')
        .then(response => response.json())
        .then(data => {
            for(let i in data){
                let title = data[i].title
                let keyframes_text = data[i].keyframes_text

                if(title === localtitle){
                    let len = Object.keys(keyframes_text).length
                    let timeStamp = [];
                    let allKeywords = []

                    for(let i=1;i<=len;i++){
                        let obj = keyframes_text[i]
                        let startTime = obj[0]
                        let endTime = obj[1]
                        let keywords= obj[2].slice(1,obj[2].length-1).split(',')

                        keywords.forEach(function(key,j) {
                            allKeywords.push(key)
                            key = key.toUpperCase()

                            let searchKeyword = document.getElementById('videDetails-search').value

                            if(searchKeyword === '')
                                return
                            if(key.includes(searchKeyword))
                                timeStamp.push(startTime)
                        })
                    }   
                }
            }

            document.body.innerHTML = ''

            let videoDetails = document.createElement('div')
            videoDetails.className = 'videoDetails'

            let videoDetails_video = document.createElement('div')
            videoDetails_video.className = 'videoDetails-video'

            document.body.append(videoDetails)
            document.body.append(videoDetails_video)

            let searchDiv = document.createElement('div')
            searchDiv.className = 'videoDetails-searchDiv'

            var input = document.createElement("input");
            input.type = "text";
            input.className = "videDetails-search";
            document.body.appendChild(input);

            
            var searchBtn = document.createElement("button");
            var text = document.createTextNode("Search");
            searchBtn.className = "videDetails-searchBtn"
            searchBtn.appendChild(text)
            searchBtn.onclick = {handleClick}
            document.body.appendChild(searchBtn);

            document.body.append(searchDiv)



            document.body.append("Hello")
        })
        .catch(error => {
            console.error('Error fetching JSON data:', error);
        });
}

export default VideoDetails










