import React, { useRef, useState, useEffect } from "react";
import Chunk from "./Chunk";

const ProgressBar = ({  duration, onTimeUpdate, chunks }) => {
  const [currentTime, setCurrentTime] = useState(0);
  const progressBarRef = useRef(null);

  const handleProgressClick = (event) => {
    const progressBar = progressBarRef.current;
    const progressWidth = progressBar.offsetWidth;
    const progressClicked = event.clientX - progressBar.getBoundingClientRect().left;
    const newTime = (progressClicked / progressWidth) * duration;
    onTimeUpdate(newTime);
    //console.log(progressClicked)
  };

  const progress = (currentTime / duration) * 100;
  
  let chunksToShow = [];

  chunks.forEach((chunk) => {
    const chunkClass =
      progress >= chunk.start && progress <= chunk.end
        ? "chunk active"
        : "chunk";

    if (progress >= chunk.end) {
      chunksToShow.push(
        <div className={chunkClass} key={`${chunk.start}-${chunk.end}`} />
      );
    }
  });

  useEffect(() => {
    const progressBar = progressBarRef.current;

    const handleTimeUpdate = () => {
      setCurrentTime(progressBar.currentTime);
    };
    
    progressBar.addEventListener("timeupdate", handleTimeUpdate);
  }, []);

  const progressBarStyles = {
    height: "10px",
    width: "100%",
    position: "relative",
    borderRadius: "10px",
    overflow: "hidden"
  };

  const renderChunks = () => {
    return chunks.map((chunk, index) => (
      <Chunk key={index} {...chunk} duration={duration} />
    ));
  };

  return (
    <div
      className="progress-bar"
      style={progressBarStyles}
      ref={progressBarRef}
      onClick={handleProgressClick}
    >
      {renderChunks()}
    </div>
  );
};

export default ProgressBar;
