import axios from "axios"
import React, { useState, useEffect } from "react"
import { Link } from "react-router-dom"

function List(props) {
  let searchData = props.searchData
  let jsonPath, labelCount=0, allLabelCount=0
  const [myData, setMyData] = useState([])
  const [myAlllabel, setAllLabels] = useState(true)
  const [mylabel, setLabels] = useState(null)
  const [selectedTopic, setSelectedTopic] = useState(null)

  if ("INDIA".includes(searchData)) {
    jsonPath = "./india-cluster.json"
  } else if ("PHYSICS".includes(searchData)) {
    jsonPath = "./physics-cluster.json"
  } else if ("LAW".includes(searchData)) {
    jsonPath = "./law-cluster.json"
  }

  useEffect(() => {
    axios.get(jsonPath).then((res) => setMyData(res.data))
  }, [searchData])

  const [activeButton, setActiveButton] = useState(null);

  const handleListClick = (event, labels, documents) => {
    setSelectedTopic(labels)
    var currentButton = event.target;
    currentButton.style.backgroundColor = "grey";
    currentButton.style.border = "2px solid black";
    currentButton.style.height = "2rem"
    currentButton.style.width = "10rem"

    if(activeButton && activeButton !== currentButton) {
      activeButton.style.backgroundColor = "";
      activeButton.style.border = "";
      activeButton.style.height = ""
      activeButton.style.width = ""
    }

    setActiveButton(currentButton);
    setAllLabels(false);
    setLabels(labels);
  };

  const linkStyle = {
    color: 'black',
    fontSize: '18px'
  };

  const handleClick = (title,videoFileName) => {
    localStorage.setItem('title', title);
    localStorage.setItem('videoFileName', videoFileName);
  }

  const displayDiv = () => {
    var div = document.getElementById("myDiv");
  div.style.display = "block";
  }

  const displayContent = (title,content,transcript,videoFileName) => {
    return (
      <div className="displaylistContent">
        <h5 id="chart-title">{title}</h5>
        <p>{content}</p>
        <button onClick={displayDiv}>Show div</button>
        <button id='view-VideoDetails' onClick={handleClick.bind(this,title,videoFileName)}>
          <Link to="/videoDetails" style={linkStyle} target="_blank">Watch Video</Link>
        </button>
      </div>
    )
  }

  return (
    <div className="displayList">
      <div id="myDiv" style="display: none;">Hello, world!</div>
      <div className="list-content" id="listView">
        {myData.map((post) => {
          let { labels, documents } = post

          labels = labels[0]

          for(let i=0;i<documents.length;i++){
            if(mylabel === labels) 
              labelCount++
            allLabelCount++
          }

          return (
            <div>
                <button id="list-btn" onClick={(event) => handleListClick(event, labels, documents)}>
                  <h5>{labels}</h5>
                </button>
            </div>
          )
        })}
      </div>

      {myAlllabel && (
        <div className="list-content" id="list-content">
          <div className="label-count"><h5>All retrieved results: {allLabelCount}</h5> </div>
          <div>
            {myData.map((post) => {
              let { labels, documents } = post

              labels = labels[0]

              return documents.map((doc, i) => {
                let title = doc.title
                let content = doc.content
                let transcript = doc.transcript
                let videoFileName = './rbi/' + doc.video_File_Name + '.mp4'
                let uniqueId = Math.floor((Math.random() * 50) + 1)
                
                return (<div key={i}> {displayContent(title,content,transcript,videoFileName)} </div>)
              })
            })}
          </div>
        </div>
      )}

      {mylabel && (
        <div className="list-content" id="list-content">
          <div className="label-count"><h5>All retrieved results for topic {selectedTopic}: {labelCount}</h5> </div>
          <div>
            {myData.map((post) => {
              let { labels, documents } = post

              labels = labels[0]

              if(mylabel === labels) {
                return documents.map((doc, i) => {
                  let title = doc.title
                  let content = doc.content
                  let transcript = doc.transcript
                  let videoFileName = './rbi/' + doc.video_File_Name + '.mp4'
                  let uniqueId = Math.floor((Math.random() * 50) + 1)
                
                  return (<div key={i}> {displayContent(title,content,transcript,videoFileName)} </div>)
                })
              }
            })}
          </div>
        </div>
      )}
    </div>
  )
}

export default List
