import axios from "axios";
import ReactPlayer from "react-player";
import React, { useState, useEffect} from "react";

const removeDuplicateKeywords = () => {
  const list = [];
  const set = new Set();

  return {
    push(element) {
      if (!set.has(element)) {
        list.push(element);
        set.add(element);
      }
    },
    get() {
      return list;
    }
  };
}

const [showPopup, setShowPopup] = useState(false);

const handleMouseOver = () => {
  setShowPopup(true);
};

const handleMouseOut = () => {
  setShowPopup(false);
};

const popupStyle = {
  position: 'absolute',
  display: showPopup ? 'block' : 'none',
  backgroundColor: '#f1f1f1',
  padding: '10px',
  border: '1px solid #ccc',
};

function VideoDetails() {
  const [myData, setMyData] = useState([])
  const [searchKeyword, setSearchKeyword] = useState(0);
  const [instance, setInstance] = useState(0);

  let allKeywords = removeDuplicateKeywords();
  let localtitle = localStorage.getItem('title')
  let localfaculty = localStorage.getItem('faculty')
  let localdomain = localStorage.getItem('domain')
  let video_url = localStorage.getItem('videoUrl')
  let temp=''

  video_url = video_url.slice(0,25) + "uc?export=view&id=" + video_url.slice(32,65)
  
  const handleStart = (startTime) => {
    if(instance) {
      if(isNaN(startTime)) {
        startTime = "0.0";
      }
      instance.seekTo(startTime);
    }
  };

  useEffect(() => {
    axios.get('./india.json')
      .then((res) => setMyData(res.data)
    )
  }, []);

  const myDiv = document.createElement('div');
  myDiv.className = 'keywordContainer'

  const displayTimeStamp = (searchKeyword,orginaltimeStamp) => {
    myDiv.innerHTML = ''
    const timeStamp = new Set(orginaltimeStamp);

    if(temp !== searchKeyword){
      const btn = document.getElementById('videoDetails-searchBtn');
      btn.addEventListener('click', function handleClick() {
        myDiv.replaceChildren();
        myDiv.style.border = 'none'
      });
      myDiv.replaceChildren();
      //myDiv.style.border = "2px solid black"
    }
    
    for(let startTime of timeStamp.values()){
      let originalTime = startTime
      startTime = startTime.split(':')

      let hr = parseInt(startTime[0], 10);
      let min = parseInt(startTime[1], 10);
      let sec = parseInt(startTime[2], 10);

      startTime = (hr * 3600) + (min * 60) + sec

      const photoDiv = document.createElement('div');
      photoDiv.className = 'photodiv-container';
      photoDiv.onclick = handleStart.bind(this,startTime);
      
      let img = document.createElement("img");
      /*
      let imgHover = document.createElement("img");
      imgHover.src = "image.png";
      imgHover.className = 'imgHoverEffect'
      myDiv.appendChild(imgHover);
      */

      //img.src = "no-image.png";
      //img.src = "image.png";
      
      if(searchKeyword.includes('BANK'))
        img.src = 'rbi/001.jpg'
      else if(searchKeyword.includes('INTRODUCTION'))
        img.src = 'rbi/002.jpg'
      else if(searchKeyword.includes('FUNCTIONS OF RBI'))
        img.src = 'rbi/003.jpg'
      else if(searchKeyword.includes('FUNCTIONS OF RBI'))
        img.src = 'rbi/004.jpg'
      else if(searchKeyword.includes('POLICY RATE'))
        img.src = 'rbi/005.jpg'
      else if(searchKeyword.includes('POLICY RATE'))
        img.src = 'rbi/006.jpg'
      else if(searchKeyword.includes('POLICY RATE'))
        img.src = 'rbi/007.jpg'
      else if(searchKeyword.includes('RESERVE RATIOS'))
        img.src = 'rbi/008.jpg'
      else if(searchKeyword.includes('RESERVE RATIOS'))
        img.src = 'rbi/009.jpg'
      else if(searchKeyword.includes('THANK'))
        img.src = 'rbi/010.jpg'

      photoDiv.appendChild(img);

      const myButton = document.createElement('button');
      myButton.innerHTML = originalTime;
      myButton.id = 'seekToBtn';
      myButton.onclick = handleStart.bind(this,startTime);

      photoDiv.append(myButton);
      myDiv.append(photoDiv);
    }
        
    document.body.append(myDiv);  
  }

  const handleSearch = () => {
    let selectedkeyword = document.getElementById('inputVal').value
    
    selectedkeyword = selectedkeyword.toUpperCase()

    if(selectedkeyword === "Default")
        selectedkeyword = ''
    setSearchKeyword(selectedkeyword)
  }

  return (
    <div className="videoDetails">  
      <div className="videoDetails-data">
        {
          myData.map((post) => {
            let { title, faculty, domain, keyframes_text } = post

            title = title.toUpperCase()
            faculty = faculty.toUpperCase()
            domain = domain.toUpperCase()
            localtitle = localtitle.toUpperCase()
            localfaculty = localfaculty.toUpperCase()
            localdomain = localdomain.toUpperCase() 

            if((title === localtitle)){
              let len = Object.keys(keyframes_text).length
              let timeStamp = [];

              for(let i=1;i<=len;i++){
                let obj = keyframes_text[i]
                let startTime = obj[0]
                let endTime = obj[1]
                let keywords= obj[2].slice(1,obj[2].length-1).split(',')

                keywords.forEach(function(key,j) {
                  let slicedKey = key.slice(2,key.length-1)
                  allKeywords.push(slicedKey)
                  key = key.toUpperCase()

                  if(searchKeyword === '')
                    return
                  if(key.includes(searchKeyword))
                    timeStamp.push(startTime)
                })
              }

              return (<div>{displayTimeStamp(searchKeyword,timeStamp)}</div>)
            }
          })
        }
      </div>

      <div className="videoDetails-video">
        <ReactPlayer
          ref={setInstance}
          url={video_url}
          playing = {false}
          controls
          onStart={handleStart}
        />
      </div> 

      <div className="videoDetails-btn">
        <div className="keywords-container">
          <input id='inputVal' list="select-keyword"/>

          <datalist id='select-keyword' placeholder="Pick a keyword">
            <option id='option-keyword' value="">Select a keyword</option>
              { 
                allKeywords.get().sort().map((option, index) => {
                  return <option key={index} > {option} </option>
                })
              }
          </datalist >

          <button className="videoDetails-searchBtn" id="videoDetails-searchBtn" onClick={handleSearch}>Search</button>
        </div>
      </div> 
    </div>
  );
}

export default VideoDetails;
