import axios from "axios";
import { useState, useEffect } from "react";
import { PieChart } from "react-minimal-pie-chart";

const IndiaCluster = () => {
    const [myData, setMyData] = useState([])
    const allLabels = []

    let data = [
        { title: "One", value: 1, color: "#FFC074" },
        { title: "Two", value: 2, color: "#A2D2FF" },
        { title: "Three", value: 3, color: "#FF9292" },
    ];

    useEffect(() => {
        axios.get('./india-clusters.json')
          .then((res) => setMyData(res.data)
        )
    }, [0]);

    return (  
        <div className="clusters">
            <div>
                {
                    myData.map((post) => {
                        let { labels,documents} = post
                        
                        labels = labels[0]
                        allLabels.push(labels)

                        for(let i=0;i<documents.length;i++){
                            let title = documents[i].title
                            let content = documents[i].content
                        }

                        <div style={{backgroundColor:"#DAE5D0"}}>
                <PieChart
                    animate
                    animationDuration={40}
                    animationEasing="ease-in"
                    center={[50, 50]}
                    data={data}
                    lineWidth={15}
                    lengthAngle={360}
                    paddingAngle={0}
                    radius={50}
                    rounded
                    startAngle={0}
                    viewBoxSize={[100, 100]}
                    labelStyle={{
                        fontSize: "6px",
                        fontColor: "FFFFFA",
                        fontWeight: "500",
                        fontFamily: "monospace"
                    }}
                    label={(data) => data.dataEntry.title}
                    labelPosition={70}
                />
            </div>   
                    })
                }
            </div> 

            
        </div>
    )
}

export default IndiaCluster

