import axios from "axios";
import React, { useState, useEffect, useRef} from "react";
import ProgressBar from "./Progressbar/ProgressBar";

const removeDuplicateKeywords = () => {
  const list = [];
  const set = new Set();

  return {
    push(element) {
      if (!set.has(element)) {
        list.push(element);
        set.add(element);
      }
    },
    get() {
      return list;
    }
  };
}

function VideoDetails() {
  const [myData, setMyData] = useState([])
  const [searchKeyword, setSearchKeyword] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const videoRef = useRef(null);

  let allKeywords = removeDuplicateKeywords();
  let titleofVideo = localStorage.getItem('title')
  let localvideoFileName = localStorage.getItem('videoFileName')
  let temp=''

  const handleSeekTo = (time) => {
    if (videoRef.current) {
      videoRef.current.currentTime = time;
    }
  };

  useEffect(() => {
    axios.get('./india.json')
      .then((res) => setMyData(res.data)
    )
  }, []);

  const toSeconds = (time) => {
    let seconds = time.split(':')

    let hr = parseInt(seconds[0], 10);
    let min = parseInt(seconds[1], 10);
    let sec = parseInt(seconds[2], 10);
    
    seconds = (hr * 3600) + (min * 60) + sec;

    return seconds
  }
  
  const myDiv = document.createElement('div');
  myDiv.className = 'keywordContainer'

  const displayTimeStamp = (searchKeyword,orginaltimeStamp,imgSrc) => {
    myDiv.innerHTML = ''
    const timeStamp = new Set(orginaltimeStamp);

    if(temp !== searchKeyword){
      const btn = document.getElementById('videoDetails-searchBtn');
      btn.addEventListener('click', function handleClick() {
        myDiv.replaceChildren();
        myDiv.style.border = 'none'
      });
      myDiv.replaceChildren();
      //myDiv.style.border = "2px solid black"
    }
    
    for(let startTime of timeStamp.values()){
      let originalTime = startTime
      startTime = toSeconds(startTime)

      const photoDiv = document.createElement('div');
      photoDiv.className = 'photodiv-container';
      photoDiv.onclick = handleSeekTo.bind(this,startTime);
      
      let img = document.createElement("img");
      img.className = 'keywordImage'
      
      let imgHover = document.createElement("img");
      //imgHover.src = "image.png";
      imgHover.className = 'imgHoverEffect'


      img.src = 'rbi/'+imgSrc;
      imgHover.src = imgSrc;

      photoDiv.appendChild(img);
      //photoDiv.appendChild(imgHover);

      const myButton = document.createElement('button');
      myButton.innerHTML = originalTime;
      myButton.id = 'seekToBtn';
      myButton.onclick = handleSeekTo.bind(this,startTime);

      photoDiv.append(myButton);
      myDiv.append(photoDiv);
      
      photoDiv.addEventListener('mouseover', () => {
        imgHover.style.display = 'block';
        imgHover.style.left = photoDiv.offsetLeft + 'px';
        imgHover.style.bottom = (photoDiv.offsetTop) + 'px';
        //imgHover.style.bottom = (photoDiv.offsetTop + photoDiv.offsetHeight) + 'px';
        //imgHover.style.bottom = (window.innerHeight - photoDiv.offsetTop) + 'px';
      });
      
      photoDiv.addEventListener('mouseout', () => {
        imgHover.style.display = 'none';
      });
    }
        
    document.body.append(myDiv);  
  }

  const handleSearch = () => {
    let selectedkeyword = document.getElementById('inputVal').value
    
    selectedkeyword = selectedkeyword.toUpperCase()

    if(selectedkeyword === "Default")
        selectedkeyword = ''
    setSearchKeyword(selectedkeyword)
  }

  const chunks = [];

  const handleTimeUpdate = (newTime) => {
    videoRef.current.currentTime = newTime;
    setCurrentTime(newTime);
  };

  const handleDurationChange = () => {
    setDuration(videoRef.current.duration);
  };

  const handleFullScreen = () => {
    if(videoRef.current) {
      videoRef.current.requestFullscreen();
    }
  };

  return (
    <div className="videoDetails">  
      <div className="videoDetails-data">
        {
          myData.map((post) => {
            let { title, keyframes_text, chapters_list, image_metadata } = post            
            localvideoFileName = localvideoFileName.toUpperCase() 

            let len = Object.keys(chapters_list).length
            let timeStamp = [];
            let imgSrc;

            for(let i=1;i<=len;i++){
              let obj = chapters_list[i]
              let startTime = obj[0]
              let keywords = obj[1]
              console.log(keywords)

              let imageData = image_metadata[i]
              let imagePath = imageData[0]

              if(keywords === searchKeyword){
                imgSrc = imagePath
              }

              allKeywords.push(keywords)
              keywords = keywords.toUpperCase()
              
              let obj2 = keyframes_text[i]
              let endTime = obj2[1]
              let startTimeSeconds = toSeconds(startTime)
              let endTimeSeconds = toSeconds(endTime)

              const colors = [ "#FF0000", "#00FF00", "#0000FF", "#FFFF00", "#FF00FF", "#00FFFF", "#800000", "#008000", "#000080", "#808000", "#800080", "#008080", "#C0C0C0", "#800000", "#FFA500", "#FFFFF0" ];
              
              if(title.includes(titleofVideo) || titleofVideo.includes(title) || (titleofVideo === title)){
                const newChunk = {
                  start: startTimeSeconds,
                  end: endTimeSeconds-1,
                  //color: colors[Math.floor(Math.random() * colors.length)],
                  color: "gray",
                  topic: keywords
                };
                chunks.push(newChunk);
              }
              
              if(searchKeyword === '')
                return
              if(keywords.includes(searchKeyword))
                timeStamp.push(startTime)
            }
            
            return (<div>{displayTimeStamp(searchKeyword,timeStamp,imgSrc)}</div>)
          })
        }
      </div>

      <div className="videoDetails-video">
        <video
          ref={videoRef}
          src={localvideoFileName}
          onTimeUpdate={() => setCurrentTime(videoRef.current.currentTime)}
          onDurationChange={handleDurationChange}
          height={360}
          controls
        />

        <button id='fullScreen' onClick={handleFullScreen}>FullScreen</button>
        
        <ProgressBar
          className="progressBar"
          currentTime={currentTime}
          duration={duration}
          onTimeUpdate={handleTimeUpdate}
          chunks={chunks}
        />
      </div> 

      <div className="videoDetails-btn">
        <div className="keywords-container">
          <input id='inputVal' list="select-keyword"/>

          <datalist id='select-keyword' placeholder="Pick a keyword">
            <option id='option-keyword' value="">Select a keyword</option>
              { 
                allKeywords.get().sort().map((option, index) => {
                  return <option key={index} > {option} </option>
                })
              }
          </datalist >

          <button className="videoDetails-searchBtn" id="videoDetails-searchBtn" onClick={handleSearch}>Search</button>
        </div>
      </div> 
    </div>
  );
}

export default VideoDetails;
