import axios from "axios";
import React, { useState, useEffect } from 'react'
import SearchBar from "./SubComponents/SearchBar";
import Video from './SubComponents/Video';
import VideoContent from './SubComponents/VideoContent';
import Pagination from "./SubComponents/Pagination";
import Charts from "./SubComponents/Charts";
import List from "./SubComponents/List";
import India from "./SubComponents/India";

function Content() {
  const [myData, setMyData] = useState([])
  
  let [searchData, setSearchData] = useState('');
  let [teacher, setTeacher] = useState('');
  let [domains, setDomains] = useState('');

  let showChartList = false

  let teacherList = []
  let domainsList = []

  const getSearchData = (data) => {
    data = data.toLowerCase()
    setSearchData(data)
  };

  if('chemistry'.includes(searchData) || 'chemistry - pesticides'.includes(searchData)){
    teacherList = ['Kalaam']
    domainsList = ['Chemistry']
  }
  else if('social'.includes(searchData) || 'law and social justice'.includes(searchData)){
    teacherList = ['Singh']
    domainsList = ['Social']
  }
  else if('history'.includes(searchData)){
    teacherList = ['Kalaam']
    domainsList = ['History']
  }

  else if('finance'.includes(searchData) || 'reserve bank'.includes(searchData)){
    teacherList = ['Singh']
    domainsList = ['Finance']
  }
  else if('history'.includes(searchData) || 'judiciary'.includes(searchData) || 'constitution'.includes(searchData)){
    teacherList = ['David']
    domainsList = ['Constitution']
  }

  else if('mathematics'.includes(searchData) || 'matrix'.includes(searchData) || 'nin-max normalization'.includes(searchData)){
    teacherList = ['Kohli']
    domainsList = ['Mathematics']
  }
  else if('physics'.includes(searchData) || 'physics - magnetism'.includes(searchData) || 'physics - raman spectroscopy'.includes(searchData)){
    teacherList = ['Singh','David']
    domainsList = ['Physics']
  }
  else if('india'.includes(searchData) || 'india - judiciary'.includes(searchData) || 'indian - constitution'.includes(searchData) || 'reserve bank of india'.includes(searchData) || 'indian national movement'.includes(searchData) || 'indian desert'.includes(searchData) || 'india - climate,vegetation and wildlife'.includes(searchData)){
    teacherList = ['Singh','David','Kalaam','Sharma']
    domainsList = ['History','Finance','Geography','Constitution']
  }
  else if('geography'.includes(searchData) || 'indian desert'.includes(searchData) || 'minerals and power resources'.includes(searchData) || 'GEOGRAPHY - COASTAL PLAINS AND ISLANDS'.includes(searchData) || 'agriculture'.includes(searchData) || 'globe - latitudes and longitudes'.includes(searchData) || 'india - climate,vegetation and wildlife'.includes(searchData) || 'human resource'.includes(searchData)){
    teacherList = ['Singh','David','Kalaam','Sharma','Chopra']
    domainsList = ['Geography']
  }
  else if('computer science'.includes(searchData) || 'cyber security'.includes(searchData) || 'ranked retrieval'.includes(searchData) || 'MACHINE LEARNING'.includes(searchData)){
    teacherList = ['Kohli','Sharma','Chopra']
    domainsList = ['Computer Science']
  }

  const onTeacherChangeHandler = () => {
    let selectedTeacher = document.getElementById('select-faculty').value
    if(selectedTeacher === "Default")
      selectedTeacher = ''
    setTeacher(selectedTeacher)
  }

  const onDomainsChangeHandler = () => {
    let selectedDomain = document.getElementById('select-domain').value
    if(selectedDomain === "Default")
      selectedDomain = ''
    setDomains(selectedDomain)
  }

  useEffect(() => {
    axios.get('./data.json')
      .then((res) => {
        setMyData(res.data)
      }
    )
  }, [])

  let [showPieChartContent,setShowPieChartContent ] = useState(false);
  let [showListContent,setShowListContent ] = useState(false);

  const handleListContent = () => {
    setShowPieChartContent(false)
    setShowListContent(true)
  }
  
  const handlePieChartContent = () => {
    setShowListContent(false)
    setShowPieChartContent(true)
  }

  const displayPieChartData = (searchData) => {
    return (
      <div>
        <div className="PieChart">
          <Charts searchData={searchData}/>
        </div>
      </div>
    )
  }

  const displayListData = (searchData) => {
    return (
      <div>
        <div className="list">
          <List searchData={searchData}/>
        </div>
      </div>
    )
  }

  const shortTranscript = (transcript, maxSentences) => {
    let i=0,count=0
    let newTranscript=''

    while(i < transcript.length){
      if(count === maxSentences)
        break
      if(transcript[i] === '.')
        count++;
      newTranscript = newTranscript + transcript[i++];
    }

    return newTranscript
  }










  const displayVideoContent = (video_url,title,duration,faculty,domain,newTranscript,transcript) => {
    return (
      <div className='videos-content'>
        <Video title={title} faculty={faculty} domain={domain} video_url={video_url}/>
        <VideoContent title={title} duration={duration} faculty={faculty} transcript={transcript} newTranscript={newTranscript}/>
      </div>
    )
  }

  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(2);

  //const indexOfLastPost = currentPage * postsPerPage;
  //const indexOfFirstPost = indexOfLastPost - postsPerPage;

  const indexOfFirstPost = (currentPage) * postsPerPage
  const indexOfLastPost = indexOfFirstPost + postsPerPage
  const currentPosts = myData.slice(indexOfFirstPost, indexOfLastPost);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  let totalPost=0;



  return (
    <>
      <div className="container"> 
        <div className="searchFilter">  
          <div className='searchBar'>
            <SearchBar onSubmit={getSearchData}/>  
          </div>   
          
          <div className='facultyDomain'>
            <div className='faculty-title'>Faculty</div>
            <div className="faculty-container">
              <select id='select-faculty' onChange={onTeacherChangeHandler}>
                <option>Default</option>
                  {
                    teacherList.map((option, index) => {
                      return <option key={index} > {option} </option>
                    })
                  }
              </select>
            </div>

            <div className='domain-title'>Domain</div>
            <div className="domain-container">
              <select id='select-domain' onChange={onDomainsChangeHandler}>
                <option>Default</option>
                  {
                    domainsList.map((option, index) => {
                      return <option key={index} > {option} </option>
                    })
                  }
              </select>
            </div>
          </div>
        </div>

        <div className="grid">
          {
            currentPosts.map((post) => {
              if(searchData !== ''){
                totalPost++;
                showChartList = true
                document.querySelector('.facultyDomain').style.display = 'grid'

                document.querySelector('#search-btn').addEventListener('click', function(){ 
                  document.getElementById("select-faculty").getElementsByTagName('option')[0].selected = 'selected';
                  document.getElementById("select-domain").getElementsByTagName('option')[0].selected = 'selected';

                  setTeacher('')
                  setDomains('')
                });

                let { domain, title, duration, faculty, video_url, transcript } = post
                
                searchData = searchData.toUpperCase()
                domain = domain.toUpperCase()
                domains = domains.toUpperCase()
                teacher = teacher.toUpperCase()
                faculty = faculty.toUpperCase() 
                title = title.toUpperCase() 

                let maxSentences = 5
                let newTranscript = shortTranscript(transcript, maxSentences);

                if(title.includes(searchData) || domain.includes(searchData) || faculty.includes(searchData)){
                  if((teacher === faculty) && (domain === domains))
                    return (<div> {displayVideoContent(video_url,title,duration,faculty,domain,newTranscript,transcript)} </div>)
                  
                  else if((teacher === faculty) && (domains === ''))
                    return (<div> {displayVideoContent(video_url,title,duration,faculty,domain,newTranscript,transcript)} </div>)
                  
                  else if((teacher === '') && ((domain === domains)))
                    return (<div> {displayVideoContent(video_url,title,duration,faculty,domain,newTranscript,transcript)} </div>)

                  else if((teacher === '') && (domain === domains))
                    return (<div> {displayVideoContent(video_url,title,duration,faculty,domain,newTranscript,transcript)} </div>)
                  
                  else if(teacher === '' && domains === '')
                    return (<div> {displayVideoContent(video_url,title,duration,faculty,domain,newTranscript,transcript)} </div>)
                } 
              }
              else if(searchData===''){
                
              }
            })
          }
          <Pagination
            postsPerPage={postsPerPage}
            totalPosts={currentPosts.length}  //myData.length currentPosts.length
            paginate={paginate}
          />
        </div>

        {
          showChartList && (
            <div className="chartList"> 
              <button id='listBtn' onClick={handleListContent}>List</button>
              <button id='pieChartBtn' onClick={handlePieChartContent}>Pie Chart</button>
              
              {showPieChartContent && (
                <div>
                  {displayPieChartData(searchData)}
                </div>
              )
              }
              {showListContent && (
                <div>
                  {displayListData(searchData)}
                </div>
              )
              }
            </div>
          )
        }
        

      </div>
    </>
  )
}

export default Content
